INSERT INTO `product_apts` VALUES (113, 1, 'Применение', 'Нанесите молочко массирующими движениями на кожу лица и шеи, затем аккуратно удалите средство с помощью влажных ватных дисков. Нанесите тоник на лицо массирующими движениями с помощью ватного диска. Рекомендуется использовать комплекс каждый день утром и вечером.', 1);
INSERT INTO `product_apts` VALUES (113, 1, 'Результаты', '&lt;p&gt;90% отметили, что комплекс отлично сужает поры и выводит из них загрязнения.&lt;/p&gt;&lt;p&gt;96% подтвердили, что комплекс делает кожу более мягкой и гладкой.&lt;/p&gt;&lt;p&gt;89% согласились, что средства, входящие в комплекс бережно заботятся о чувствительной коже, не вызывая раздражений.&lt;/p&gt;\r\n&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;\r\n', 2);
INSERT INTO `product_apts` VALUES (113, 1, 'Состав', '&lt;h3&gt;Gentle Cleansing milk&lt;/h3&gt;\r\n\r\n&lt;p&gt;Aqua, Неlianthus Annuus (Sunflower) Seed Оil, Glycerin, Mono Glyceryl Stearate PEG-100 Stearate, Cetearyl Alcohol, Capric/Caprylic Triglyceride, Ethylhexyl Stearate, Isopentyldiol, Tocopheryl Acetate, Chamomilla Recutita (Matricaria) Extract, Lavandula angustifolia Extract, Calendula Officinalis Extract, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, Sodium Polyacrylate, Phenoxyethanol, Ethylhexylglycerin, Perfume, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).\r\n&lt;/p&gt;\r\n\r\n&lt;h3&gt;Gentle Chamomile Bergamot Toner&lt;/h3&gt;\r\n\r\n&lt;p&gt;Aqua, Isopentyldiol, Coco Glucoside, Trimethylglycine, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, PEG-40 Hydrogenated Castor Oil, Saccharide Isomerate, Citric Acid, Sodium Citrate, Bergamote (Citrus aurantium bergamia) Essential Oil, Matricaria Recutita Flower Extract, Sodium Hydroxymethylglycinate, Sodium Gluconate, Parfume, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).\r\n&lt;/p&gt;', 0);
