INSERT INTO `product_apts` VALUES (120, 1, 'Результаты', '&lt;p&gt;90% констатировали улучшение самочувствия и состояния здоровья после нескольких применений комплекса.&lt;/p&gt;\r\n&lt;p&gt;100% отметили, что кожа лица и тела стала более подтянутая и упругая.&lt;/p&gt;\r\n&lt;p&gt;99% согласились, что комплекс отлично омолаживает кожу, отшелушивая ороговевшие частички и делая ее мягкой и шелковистой.&lt;/p&gt;\r\n&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;\r\n', 2);
INSERT INTO `product_apts` VALUES (140, 1, 'Состав', '&lt;h3 style=&quot;outline: 0px; line-height: 1.3; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 18px; padding: 0px; color: rgb(51, 51, 51); font-family: standard;&quot;&gt;Coffee oil Scrub&lt;/h3&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Salt, Prunus Amygdalus Dulcis (Sweet Almond) Oil, Coffee, Persea Gratissima Oil, Simmondsia Chinensis (Jojoba) Seed Oil, Parfum, Citrus Reticulata Blanco (Tangerine) Essential Oil, Tocopherol (mixed), Glyceryl Linoleate, Beta-Sitosterol, Squalene, Tocopheryl Acetate.&lt;/p&gt;&lt;h3 style=&quot;outline: 0px; line-height: 1.3; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-family: standard;&quot;&gt;Nourishing Camellia Body Oil&lt;/h3&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Prunus Amygdalus Dulcis (Sweet Almond) Oil, Camellia Oleifera Seed Oil, Tocopherol (mixed), Glyceryl Linoleate, Beta-Sitosterol, Squalene, Lavandula Angustifolia Essential Oil, Pogostemon Patchouli Essential Oil, Tocopheryl Acetate.&lt;/p&gt;', 0);
INSERT INTO `product_apts` VALUES (140, 1, 'Применение', '&lt;p style=&quot;outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Шаг 1 – глубокое очищение – скраб кофейный с маслами&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Необходимое количество скраба нанести на влажную кожу тела массажными движениями. Смыть теплой водой.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Использовать 2-3 раза в неделю.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Шаг 2 – сухой массаж при помощи щетки&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Массаж необходимо делать плавными круговыми движениями, которые стимулируют кожу, но не повреждают ее. Так как лимфа движется по телу снизу вверх, делать массаж нужно по ее току. Начинайте массаж с подошвы стоп, далее проработайте пальцы ног и ступни, затем лодыжки и голени. Переходите к бедрам, уделяя особое внимание проблемным зонам, затем массируйте спину, живот и грудь. Массаж рук начинайте от кончиков пальцев и двигайтесь вверх до плеч. Затем аккуратно помассируйте шею и голову. Будьте внимательны к зонам, где расположены лимфоузлы (пах, подмышечные впадины), а также к зоне декольте и шеи. Не массируйте лицо щеткой для тела. Рекомендуется начать массаж с 3-х минут и каждый день увеличивать время процедуры, чтобы кожа привыкла к новым ощущениям. Для достижения наилучшего эффекта необходимо проводить процедуру массажа регулярно.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Шаг 3 – питание и смягчение – масло для тела «Камелия»&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Нанесите на кожу массажными движениями, дождитесь полного впитывания продукта в кожу.&lt;/p&gt;', 0);
