INSERT INTO `product_apts` VALUES (82, 1, 'Применение', 'Нанесите маску на очищенную кожу лица и шеи и оставьте на 10-15 минут\r\nдо высыхания маски. Смойте теплой водой, затем нанесите на кожу тоник,\r\nподходящий для вашего типа кожи.\r\n\r\nИспользуйте маску 1-2 раза в неделю. Чтобы убрать воспаления\r\nрекомендуется нанести маску локально на поврежденные участки кожи и\r\nоставить на ночь.', 2);
INSERT INTO `product_apts` VALUES (82, 1, 'Результаты', '&lt;p&gt;Более 80% использовавших маску для проблемной кожи розмарин лаванда отметили изменения кожи лица в лучшую сторону.\r\n&lt;/p&gt;&lt;p&gt;Около 70% отметили, что кожа стала более эластичная и мягкая, тон кожи стал ровнее, воспалений стало значительно меньше.\r\n&lt;/p&gt;&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 3);
