INSERT INTO `product_apts` VALUES (133, 1, 'Применение', '&lt;p style=&quot;outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Нанесите достаточное количество маски на сухую кожу лица, избегая области вокруг глаз и губ, оставьте на 15-20 минут. Остатки маски смойте теплой водой. Нанесите крем.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Используйте маску 2-3 раза в неделю.&lt;/p&gt;', 1);
