INSERT INTO `product_apts` VALUES (132, 1, 'Применение', '&lt;p style=&quot;outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Нанесите маску тонким слоем на сухую кожу лица, избегая области вокруг глаз и губ, оставьте до полного высыхания. Аккуратно снимите маску по направлению снизу вверх, прихватывая край плёнки у подбородка. Тщательно смойте остатки маски теплой водой. Нанесите крем.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Используйте маску 1-2 раза в неделю.&lt;/p&gt;', 1);
