INSERT INTO `product_apts` VALUES (65, 1, 'Результат', '&lt;p&gt;98% согласились, что масло отлично впитывается в кожу, не утяжеляя ее.\r\n&lt;/p&gt;&lt;p&gt;100% заметили улучшения состояния кожи. Она стала более гладкая и эластичная.\r\n&lt;/p&gt;&lt;p&gt;99% отметили, что шелушения на коже заметно уменьшились после нескольких применений масла.\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
INSERT INTO `product_apts` VALUES (69, 1, 'Состав', 'Salt, Prunus Amygdalus Dulcis (Sweet Almond) Oil, Coffee, Persea Gratissima Oil, Simmondsia Chinensis (Jojoba) Seed Oil, Parfum, Citrus Reticulata Blanco (Tangerine) Essential Oil, Tocopherol (mixed), Glyceryl Linoleate, Beta-Sitosterol, Squalene, Tocopheryl Acetate.', 0);
INSERT INTO `product_apts` VALUES (69, 1, 'Применение', 'Нанесите скраб массажными движениями на влажную кожу. Смойте теплой водой. Для\r\nдостижения наилучшего косметического эффекта рекомендуется использовать 2-3 раза в неделю.', 1);
INSERT INTO `product_apts` VALUES (69, 1, 'Противопоказания', '<p><span style=\"color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\">Не рекомендуется использовать при:</span></p><ul><li><span style=\"color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\">беременности</span></li><li><span style=\"color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\">при проблемах с сосудами&nbsp;</span></li><li><span style=\"color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\">варикозе</span></li></ul>', 2);
INSERT INTO `product_apts` VALUES (69, 1, 'Результат', '&lt;p&gt;89% отметили, что скраб отлично подтягивает кожу и уменьшает целлюлит. &lt;/p&gt;&lt;p&gt;\r\n90% констатировали улучшения состояния кожи уже после нескольких применений средства.\r\n&lt;/p&gt;&lt;p&gt;95% заметили, что кода стала более мягкой и сияющей.\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 3);
INSERT INTO `product_apts` VALUES (111, 1, 'Состав', 'Aqua, Cetearyl Alcohol, Polysorbate 60, Capric/Caprylic Triglyceride, Shea Butter, Almond Oil, Saccharide Isomerate, Citric Acid, Sodium Citrate, Tocopheryl Acetate, Sodium Hydroxymethylglycinate, Neroli (Citrus aurantium) Essential Oil, Bergamote (Citrus aurantium bergamia) Essential Oil,  Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).', 0);
INSERT INTO `product_apts` VALUES (111, 1, 'Применение', '&lt;p&gt;Массажными движениями нанесите лосьон на чистую кожу, тщательно распределите средство по всему телу ровным слоем. Втирайте средство до полного впитывания. &lt;/p&gt;&lt;p&gt;\r\nДля достижения наилучшего эффекта рекомендуется сочетать с Гелем для Душа Средиземноморский Нероли.\r\n&lt;/p&gt;', 1);
INSERT INTO `product_apts` VALUES (111, 1, 'Результат', '&lt;p&gt;87% отметили приятную бархатистую структуру лосьона.\r\n&lt;/p&gt;&lt;p&gt;79% согласились, что лосьон имеет отличные увлажняющие свойства.\r\n&lt;/p&gt;&lt;p&gt;85% выбрали данное средство в качестве основного ухода за телом.\r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
INSERT INTO `product_apts` VALUES (108, 1, 'Состав', 'Aqua, Cetearyl Alcohol, Polysorbate 60, Capric/Caprylic Triglyceride, Shea Butter, Almond Oil, Saccharide Isomerate, Citric Acid, Sodium Citrate, Tocopheryl Acetate, Sodium Hydroxymethylglycinate, Lavandula angustifolia Oil, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).', 0);
INSERT INTO `product_apts` VALUES (108, 1, 'Применение', '&lt;p&gt;Нанесите лосьон на кожу, массажными движениями распределите средство по всему телу до полного впитывания.&lt;/p&gt;&lt;p&gt; Рекомендуется использовать после принятие душа с гелем Прованс Лаванда.&lt;/p&gt;', 1);
INSERT INTO `product_apts` VALUES (108, 1, 'Результат', '&lt;p&gt;78% отметили приятный аромат лосьона и нежную текстуру. &lt;/p&gt;&lt;p&gt;\r\n88% согласились, что средство активно борется с сухостью и раздражениями кожи. &lt;/p&gt;&lt;p&gt;\r\n89% сказали, что кожа стала более упругой и мягкой после нескольких применений. \r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
INSERT INTO `product_apts` VALUES (76, 1, 'Состав', 'Aqua, Glyceryl Stearate SE, Sunflower Seed Oil, Almond Oil, Grapeseed Oil, Ethylhexyl Stearate, Capric/Caprylic Triglyceride, Cetearyl alcohol, Isopentyldiol, Tocopheryl Acetate, Saccharide Isomerate, Citric Acid, Sodium Citrate, Sodium Polyacrylate, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%), Dehydroacetic Acid, Benzoic Acid, Tocopherol, Benzyl Alcohol, Fragrance, Hyaluronic Acid.', 0);
INSERT INTO `product_apts` VALUES (76, 1, 'Применение', 'Наносите крем ежедневно на очищенную кожу лица. Перед применением рекомендуется очищения кожи с помощью успокаивающего тоника Ромашка Бергамот. Для ухода за более чувствительной кожей вокруг глаз используйте средство Сыворотка Эликсир для кожи вокруг глаз.', 1);
INSERT INTO `product_apts` VALUES (76, 1, 'Результат', '&lt;p&gt;88% подтвердили, что после нанесения средства кожа остается увлажненной в течение всего дня.\r\n&lt;/p&gt;&lt;p&gt;89% заметили значительное улучшение общего состояния кожи после применения средства. \r\n&lt;/p&gt;\r\n\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
INSERT INTO `product_apts` VALUES (99, 1, 'Состав', '<p><span style=\"color: rgba(0, 0, 0, 0.87); font-family: Manrope, sans-serif; font-size: 16px;\">бук, волокна мексиканского кактуса.</span><br></p>', 0);
INSERT INTO `product_apts` VALUES (99, 1, 'Применение', '<p><span style=\"color: rgba(0, 0, 0, 0.87); font-family: Manrope, sans-serif; font-size: 16px;\">Массаж необходимо делать плавными круговыми движениями, которые стимулируют кожу, но не повреждают ее. Так как лимфа движется по телу снизу вверх, делать массаж нужно по ее току. </span></p><ul><li><span style=\"color: rgba(0, 0, 0, 0.87); font-family: Manrope, sans-serif; font-size: 16px;\">Начинайте массаж с подошвы стоп, далее проработайте пальцы ног и ступни, затем лодыжки и голени. </span></li><li><span style=\"color: rgba(0, 0, 0, 0.87); font-family: Manrope, sans-serif; font-size: 16px;\">Переходите к бедрам, уделяя особое внимание проблемным зонам, затем массируйте спину, живот и грудь. </span></li><li><span style=\"color: rgba(0, 0, 0, 0.87); font-family: Manrope, sans-serif; font-size: 16px;\">Массаж рук начинайте от кончиков пальцев и двигайтесь вверх до плеч. Затем аккуратно помассируйте шею и голову. </span></li><li><span style=\"color: rgba(0, 0, 0, 0.87); font-family: Manrope, sans-serif; font-size: 16px;\">Будьте внимательны к зонам, где расположены лимфоузлы (пах, подмышечные впадины), а также к зоне декольте и шеи. Не массируйте лицо щеткой для тела. </span></li><li><span style=\"color: rgba(0, 0, 0, 0.87); font-family: Manrope, sans-serif; font-size: 16px;\">Рекомендуется начать массаж с 3-х минут и каждый день увеличивать время процедуры, чтобы кожа привыкла к новым ощущениям. Для достижения наилучшего эффекта необходимо проводить процедуру массажа регулярно.</span><br></li></ul>', 1);
