INSERT INTO `product_apts` VALUES (83, 1, 'Применение', 'Нанесите маску на предварительно очищенную кожу лица и шеи ровным слоем. Держите маску -15-20 минут, затем смойте теплой водой. Для наилучшего результата применять 2-3 раза в неделю.', 2);
INSERT INTO `product_apts` VALUES (83, 1, 'Результаты', '&lt;p&gt;88% подтвердили, что кожа стала более увлажненная и цвет лица заметно\r\nвыровнялся.\r\n&lt;/p&gt;&lt;p&gt;79% согласились, что морщинок стало меньше уже через две недели\r\nиспользования маски.\r\n&lt;/p&gt;&lt;p&gt;85% отметили приятную текстуру средства и нежный аромат.\r\n&lt;/p&gt;&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней\r\n&lt;/p&gt;', 3);
