INSERT INTO `product_apts` VALUES (114, 1, 'Состав', '<h3 style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Manrope, sans-serif; border-top-left-radius: inherit; border-top-right-radius: inherit; color: rgba(0, 0, 0, 0.87);\">Rose Rejuvenating Mask</h3><p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; color: rgba(0, 0, 0, 0.87); font-size: 16px;\">Aqua, Glyceryl Stearate SE, Cetearyl Alcohol, Capric/Caprylic Triglyceride, Isopentyldiol, Ethylhexyl Stearate, Grape Seed (Vitis Vinifera) Oil Refined, Avocado (Persea Gratissima) Oil, Kaolin, Palmitoyl Tripeptide-5, Glycerin, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, Sodium Polyacrylate, Saccharide Isomerate, Citric Acid, Sodium Citrate, Tocopheryl Acetate, Hyaluronic Acid, Phenoxyethanol, Ethylhexylglycerin, Perfume, Rosa Damascena Flower Oil, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).</p><h3 style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Manrope, sans-serif; color: rgba(0, 0, 0, 0.87);\">Anti-Age Elixir</h3><p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; border-bottom-left-radius: inherit; border-bottom-right-radius: inherit; color: rgba(0, 0, 0, 0.87); font-size: 16px;\">Aqua, Palmitoyl Tripeptide-5, Isopentyldiol, Sodium Polystyrenne Sulfonate, Hydroxyethylcellulose, Hyaluronic Acid, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, Sodium Hydroxymethylglycinate, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).</p><p><span style=\"color: rgba(0, 0, 0, 0.87); font-family: Manrope, sans-serif; font-size: 18px;\">Marine Anti Age Cream 24</span><b