INSERT INTO `product_apts` VALUES (114, 1, 'Применение', '&lt;p&gt;Шаг 1: Нанесите маску на предварительно очищенную кожу лица и шеи ровным слоем. Держите маску до засыхания 10-15 минут, затем смойте теплой водой. &lt;/p&gt;&lt;p&gt;\r\nШаг 2: Нанесите сыворотку на кожу и дождитесь ее полного впитывания.\r\n&lt;/p&gt;&lt;p&gt;Шаг 3: Нанесите на кожу увлажняющий крем Морской эффект anti-age.\r\n&lt;/p&gt;&lt;p&gt;Рекомендуется использовать маску 2-3 раза в неделю, сыворотку и крем каждый день утром и вечером.\r\n&lt;/p&gt;', 1);
INSERT INTO `product_apts` VALUES (114, 1, 'Результаты', '&lt;p&gt;91% отметили, что количество морщин заметно сократилось после использования комплекса.\r\n&lt;/p&gt;&lt;p&gt;95% подтвердили, что комплекс глубоко питает кожу и делает ее более упругой и эластичной.\r\n&lt;/p&gt;&lt;p&gt;89% согласились, что средства комплекса органично дополняют друг друга благотворно влияя на состояние кожи. \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
