INSERT INTO `product_apts` VALUES (130, 1, 'Применение', '<p><span style=\"color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\">Нанесите небольшое количество сыворотки на предварительно очищенную кожу. Сыворотку нужно наносить локально на участки с высыпаниями. Используйте утром и вечером. Для наилучшего эффекта используйте в сочетании с кремом для проблемной кожи.</span><br></p>', 1);
