INSERT INTO `product_apts` VALUES (50, 1, 'Применение', 'Нанесите небольшое количество мицеллярной воды на ватный диск. Массирующими движениями с небольшим нажимом протрите кожу лица, уделяя особое внимание участкам с повышенным загрязнением. После умойтесь прохладной водой.', 1);
INSERT INTO `product_apts` VALUES (50, 1, 'Результаты', '&lt;p&gt;76% согласились, что кожа после применения продукта заметно очистилась и стала менее восприимчива к влиянию окружающей среды.\r\n&lt;/p&gt;&lt;p&gt;88% подтвердили, что мицелярная вода роза превосходно снимает макияж и успокаивает кожу после долгого дня.\r\n&lt;/p&gt;&lt;p&gt;89% отметили приятный запах продукта, который благотворно влияет на состояние всего организма и спокойствие во время сна.\r\n&lt;/p&gt;&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней\r\n&lt;/p&gt;', 2);
