INSERT INTO `product_apts` VALUES (59, 1, 'Применение', 'Нанесите пилинг скраб на кожу и помассируйте лицо, уделяя особое внимание проблемным участкам. Оставьте средство на коже на 3-5 минут, чтобы достичь эффекта пилинга. Смойте теплой водой. \r\n&lt;/p&gt;&lt;p&gt;\r\nПрименять один раз в неделю. \r\n&lt;/p&gt;&lt;p&gt;\r\nПосле применения рекомендуется нанести на кожу Успокаивающий Тоник Ромашка Бергамот.', 2);
INSERT INTO `product_apts` VALUES (59, 1, 'Противопоказания', '<p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; border-top-left-radius: inherit; border-top-right-radius: inherit; color: rgba(0, 0, 0, 0.87); font-size: 16px;\"><span style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63);\">Не рекомендуется использовать при:</span></p><ul style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px 0px 0px 24px; margin-bottom: 0px; font-family: Manrope, sans-serif; border-bottom-left-radius: inherit; border-bottom-right-radius: inherit; color: rgba(0, 0, 0, 0.87); font-size: 16px;\"><li style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: inherit;\"><span style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63);\">акне</span></li><li style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: inherit;\"><span style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63);\">розацеа</span></li><li style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: inherit;\"><span style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63);\">герпесе</span></li><li style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: inherit;\"><span style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63);\">чувствительности кожи и чрезмерной сухости </span></li><li style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: inherit;\"