INSERT INTO `product_apts` VALUES (59, 1, 'Результаты', '&lt;p&gt;78% отметили приятную текстуру и нежный аромат средства.\r\n&lt;/p&gt;&lt;p&gt;90% заметили улучшения цвета лица и сужение пор.\r\n&lt;/p&gt;&lt;p&gt;88% согласились, что пилинг скраб делает кожу менее жирной и восприимчивой к\r\nзагрязнениям.\r\n&lt;/p&gt;&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней\r\n&lt;/p&gt;', 4);
INSERT INTO `product_apts` VALUES (63, 1, 'Состав', 'Mediterranean Sea Salt, Rosa Centifolia Flower, Parfum', 0);
INSERT INTO `product_apts` VALUES (63, 1, 'Применение', 'Растворите 100-150 г. соли в ванне с теплой водой (36-38°С), принимайте ванну в течение 10-15 минут, затем нанесите на кожу лосьон для тела Biothal для более глубокого увлажнения и достижения лучшего косметического эффекта.', 1);
INSERT INTO `product_apts` VALUES (63, 1, 'Результат', '&lt;p&gt;89% отметили приятный аромат соли для ванн, который расслабляет и успокаивает.&lt;/p&gt;&lt;p&gt;\r\n90% согласились, что кожа стала более эластичной и мягкой, после принятия ванн с солью.&lt;/p&gt;&lt;p&gt;\r\n87% почувствовали благотворное влияние ванн с солью на  общее состояние организма. \r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
