INSERT INTO `product_apts` VALUES (64, 1, 'Применение', '&lt;p&gt;Нанести массажными движениями на проблемные участки тела. Можно использовать в комплекте с антицеллюлитными средствами Biothal.&lt;/p&gt;', 1);
INSERT INTO `product_apts` VALUES (64, 1, 'Результат', '&lt;p&gt;90% согласились, что кожа стала более упругой и эластичной после нескольких применений\r\nсредства.\r\n&lt;/p&gt;&lt;p&gt;97% отметили отличное смягчающее действие масла.\r\n&lt;/p&gt;&lt;p&gt;89% констатировали полное избавление от «ямочек» на коже, после нескольких применений\r\nмасла.&lt;/p&gt;\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
INSERT INTO `product_apts` VALUES (67, 1, 'Состав', 'Ingredients: Aqua, Aloe Barbadensis (Aloe Vera) Leaf Juice, Niacinamide, Calcium Pantothenate, Sodium Ascorbyl Phosphate, Tocopheryl Acetate, PyridoxinE HCl, Maltodextrin, Sodium Starch Octenylsuccinate, Silica, Isopentyldiol, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, Sodium Hydroxymethylglycinate, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%), Hydroxyethylcellulose, Sodium Polystyrene Sulfonate, Hyaluronic Acid, Hydroxymethylglycinate.', 0);
INSERT INTO `product_apts` VALUES (67, 1, 'Применение', 'Наносите сыворотку на кожу вокруг глаз утром и вечером. После нанесите на лицо крем подходящий для вашей кожи.', 1);
INSERT INTO `product_apts` VALUES (67, 1, 'Результат', '&lt;p&gt;90% согласились, что темные круги под глазами значительно осветлились после нескольких применений продукта. &lt;/p&gt;&lt;p&gt;\r\n87% отметили, что кожа стала более мягкой и эластичной, после применения сыворотки.\r\n&lt;/p&gt;&lt;p&gt;98% констатировали значительное уменьшение количества и глубины морщин.  \r\n&lt;/p&gt;\r\n\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
INSERT INTO `product_apts` VALUES (71, 1, 'Состав', 'Aqua, Cocamidopropyl Betaine, Sodium Lauroyl Methyl Isethionate, Coco Glucoside, PEG-7 Glyceryl Cocoate, PEG-200 Glyceryl Palmate, Acetamidoethoxyethanol, Hydrolyzed Wheat Protein, Neroli (Citrus aurantium) Essential Oil, Bergamote (Citrus aurantium bergamia) Essential Oil, EDTA, Methylchloroisothiazolinone, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).', 0);
INSERT INTO `product_apts` VALUES (71, 1, 'Применение', '&lt;p&gt;Массирующими движениями нанесите гель на тело во время принятия душа или ванной. Смойте средство водой после применения. &lt;/p&gt;&lt;p&gt; \r\nДля достижения наилучшего эффекта, рекомендуется использовать в паре с лосьоном для тела Средиземноморский Нероли. \r\n&lt;/p&gt;', 1);
INSERT INTO `product_apts` VALUES (71, 1, 'Результат', '&lt;p&gt;91% отметили стойкий приятный аромат геля.\r\n&lt;/p&gt;&lt;p&gt;87% решили использовать средство, как базовый компонент ухода за телом.\r\n&lt;/p&gt;&lt;p&gt;79% отметили изменения кожи в лучшую сторону уже после нескольких применений. \r\n&lt;/p&gt;\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
INSERT INTO `product_apts` VALUES (72, 1, 'Состав', 'Aqua, Isopentyldiol, Coco Glucoside, Trimethylglycine, Laminaria saccharina extract, Ulva Lactuca Extract, Chondrus crispus extract, Fucus vesiculosus Extract, PEG-40 Hydrogenated Castor Oil, Saccharide Isomerate, Citric Acid, Sodium Citrate, Bergamote (Citrus aurantium bergamia) Essential Oil, Matricaria Recutita Flower Extract, Sodium Hydroxymethylglycinate, Sodium Gluconate, Parfume, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).', 0);
INSERT INTO `product_apts` VALUES (72, 1, 'Применение', 'Нанесите тоник на лицо массирующими движениями с помощью ватного диска. Легкий аромат ромашки и бергамота поможет вам успокоиться и расслабиться.', 1);
INSERT INTO `product_apts` VALUES (72, 1, 'Результаты', '&lt;p&gt;80% отметили, что после применения продукта кожа стала светлее и покраснения пропали.\r\n&lt;/p&gt;&lt;p&gt;75% согласились, что состояние кожи заметно улучшилось, она стала более сияющая и мягкая.\r\n&lt;/p&gt;&lt;p&gt;89% выбрали данное средство, как базовый уход за кожей на каждый день. \r\n&lt;/p&gt;&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней\r\n&lt;/p&gt;', 3);
