INSERT INTO `product_apts` VALUES (75, 1, 'Применение', 'Ежедневно наносите несколько капель сыворотки на кожу лица утром или вечером. Эликсир прекрасно подходит, как праймер под макияж.', 1);
INSERT INTO `product_apts` VALUES (75, 1, 'Результат', '&lt;p&gt;89% отметили, что кожа стала более гладкой и мягкой.&lt;/p&gt;&lt;p&gt;\r\n90% констатировали глубокую напитанность и увлажненность кожи.\r\n&lt;/p&gt;&lt;p&gt;78% согласились, что цвет кожи стал более здоровым.\r\n&lt;/p&gt;\r\n\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
