INSERT INTO `product_apts` VALUES (80, 1, 'Применение', 'Нанесите массажными движениями на влажную кожу. Смойте теплой водой. Для достижения наилучшего косметического эффекта рекомендуется использовать 2-3 раза в неделю.', 1);
