INSERT INTO `product_apts` VALUES (104, 1, 'Применение', 'Нанести гель на влажную кожу предварительно вспенив в ладонях, либо\r\nиспользуя мочалку для душа. Тщательно смыть водой после нанесения.\r\n\r\nПосле применения рекомендуется нанести на кожу Лосьон Королевский \r\nПачули для достижения наилучшего эффекта.', 1);
INSERT INTO `product_apts` VALUES (104, 1, 'Результаты', '&lt;p&gt;95% заметили, что кожа стала более нежная и бархатистая после нескольких\r\nприменений средства.\r\n&lt;/p&gt;&lt;p&gt;87% согласились, что кожа перестала сохнуть и стала более упругой и\r\nэластичной.\r\n&lt;/p&gt;&lt;p&gt;86% отметили нежный аромат средства, который остается на теле после\r\nпринятия душа.\r\n&lt;/p&gt;&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней\r\n&lt;/p&gt;', 3);
