INSERT INTO `product_apts` VALUES (116, 1, 'Результат', '&lt;p style=&quot;outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;90% отметили, что кожа стала заметно более упругой и мягкой, после применения комплекса.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;98% согласились, что комплекс помог им избавиться от «апельсиновой корки» на бедрах.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;89% констатировали улучшение состояния кожи, после нескольких применений комплекса.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней&lt;/p&gt;', 4);
INSERT INTO `product_apts` VALUES (117, 1, 'Состав', '&lt;h3 style=&quot;outline: 0px; line-height: 1.3; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 18px; padding: 0px; color: rgb(51, 51, 51); font-family: standard;&quot;&gt;Anti Cellulite Active Body oil&lt;/h3&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Prunus Amygdalus Dulcis (Sweet Almond) Oil, Olea Europaea (Olive) Husk Oil, Triticum Мulgare (Wheat) Germ Oil, Prunus Persica (Peach) Kernel Oil, Tocopherol (mixed), Glyceryl Linoleate, Beta-Sitosterol, Squalene, Grapefruit (Citrus paradisi) Essential Oil, Lemon (Citrus Limon) Essential Oil, Juniperus Communis Essential Oil, SC-CO2 Extracts (Fucus Vesiculosus Extract, Laminaria Saccharina Extract, Horse Chestnut Extract, Equisetum Arvense Extract, Hedera Helix (Ivy) Extract, Coffea Arabica (Coffee) Seed Extract), Tocopheryl Acetate.&lt;/p&gt;&lt;h3 style=&quot;outline: 0px; line-height: 1.3; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-family: standard;&quot;&gt;Coffee oil Scrub&lt;/h3&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Salt, Prunus Amygdalus Dulcis (Sweet Almond) Oil, Coffee, Persea Gratissima Oil, Simmondsia Chinensis (Jojoba) Seed Oil, Parfum, Citrus Reticulata Blanco (Tangerine) Essential Oil, Tocopherol (mixed), Glyceryl Linoleate, Beta-Sitosterol, Squalene, Tocopheryl Acetate.&lt;/p&gt;&lt;h3 style=&quot;outline: 0px; line-height: 1.3; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-family: standard;&quot;&gt;Intensive Anti Cellulite Body Cream&lt;/h3&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;Aqua, Green Coffee Extract (Coffea Arabica), Isopentyldiol, Vitamin B3 (Niacinamide), Sodium Polyacrylate, Caffeine, Laminaria Saccharina Extract, Fucus Vesiculosus Extract, Ulva Lactuca Extract, Chondrus Crispus Extract, Citrus Paradisi (Grapefruit) Oil, Juniperberry Oil (Juniperus communis), Phenoxyethanol, Ethylhexylglycerin, Maris Sal (MgCL2 – 8-15%, KCI – 2-6%, NaCl – 60-90%, CaCL2 – 0,1-0,2%).&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;&lt;br&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.4pt;line-height:normal&quot;&gt;&lt;span style=&quot;font-size:14.5pt;font-family:&amp;quot;standard&amp;quot;,serif;mso-fareast-font-family:\r\n&amp;quot;Times New Roman&amp;quot;;mso-bidi-font-family:&amp;quot;Times New Roman&amp;quot;;color:#29253F&quot;&gt;Drainage\r\nBrush dry Massage&lt;/span&gt;&lt;span style=&quot;font-size:12.0pt;font-family:&amp;quot;Times New Roman&amp;quot;,serif;\r\nmso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;\r\n\r\n&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.4pt;line-height:normal&quot;&gt;&lt;span style=&quot;font-size:12.0pt;font-family:&amp;quot;standard&amp;quot;,serif;mso-fareast-font-family:\r\n&amp;quot;Times New Roman&amp;quot;;mso-bidi-font-family:&amp;quot;Times New Roman&amp;quot;;color:#29253F&quot;&gt;Дуб,\r\nмексиканский кактус.&lt;/span&gt;&lt;span style=&quot;font-size:12.0pt;font-family:&amp;quot;Times New Roman&amp;quot;,serif;\r\nmso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;', 1);
INSERT INTO `product_apts` VALUES (117, 1, 'Применение', '&lt;p class=&quot;MsoNormal&quot; style=&quot;line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;Рекомендуем\r\nиспользовать скраб отдельно от дренажной щетки, а вот масло — обязательный\r\nучастник Вашего эффективного дуэта, так как многократно усиливает действие и\r\nскраба, и щетки.&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left: 36pt; text-indent: -18pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;!--[if !supportLists]--&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 10pt; font-family: Symbol;&quot;&gt;·&lt;span style=&quot;font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &amp;quot;Times New Roman&amp;quot;;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;Выполняйте&lt;/span&gt;&lt;span style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;массаж дренажной щёткой&lt;/span&gt;&lt;span style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;до принятия душа, на сухую кожу. Начните&lt;/span&gt;&lt;span style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;массаж с ног, постепенно поднимаясь выше. Круговыми\r\nдвижениями обработайте&lt;/span&gt;&lt;span style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;бедра и ягодицы. &lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;Будьте\r\nвнимательны к зонам, где расположены лимфоузлы (пах, подмышечные впадины), а также\r\nк зоне декольте и шеи.&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt; В завершение&lt;/span&gt;&lt;span style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;процедуры помассируйте&lt;/span&gt;&lt;span style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;руки и плечи легкими скользящими движениями. После сухого\r\nмассажа необходимо принять прохладный душ, чтобы смыть частички эпидермиса и\r\nохладить кожу.&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt; Рекомендуется начать массаж с 3-х минут\r\nи каждый день увеличивать время процедуры, чтобы кожа привыкла к новым\r\nощущениям. Для достижения наилучшего эффекта необходимо проводить процедуру\r\nмассажа регулярно. После массажа увлажнить кожу кремом.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left: 36pt; text-indent: -18pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, serif; font-size: 14pt;&quot;&gt;&lt;u&gt;Выполнять массаж сухой щеткой рекомендуется не более двух раз в\r\n     неделю.&lt;/u&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left: 36pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;ul type=&quot;disc&quot;&gt;\r\n &lt;li class=&quot;MsoNormal&quot; style=&quot;line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;span style=&quot;font-size:14.0pt;mso-bidi-font-size:12.0pt;font-family:&amp;quot;Times New Roman&amp;quot;,serif;\r\n     mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;&quot;&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:14.0pt;mso-bidi-font-size:12.0pt;font-family:&amp;quot;Times New Roman&amp;quot;,serif;\r\n     mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;mso-ansi-language:RU&quot;&gt;Нанесите\r\n     скраб на распаренную кожу тела круговыми массажными движениями, уделяя\r\n     особое внимание проблемным зонам. Оставьте на 5-10 минут для более\r\n     интенсивного воздействия, смойте теплой водой. После использования\r\n     увлажнить кремом.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-left: 36pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&lt;u&gt;Использовать скраб\r\nрекомендуется не более 2-&lt;/u&gt;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&lt;u&gt;3 раз\r\nв неделю.&lt;/u&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:14.0pt;mso-bidi-font-size:\r\n12.0pt;line-height:107%;mso-ansi-language:RU&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;ul&gt;&lt;li style=&quot;text-align: justify; text-indent: -18pt;&quot;&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:\r\n14.0pt;mso-bidi-font-size:12.0pt;line-height:107%;font-family:Symbol;\r\nmso-fareast-font-family:Symbol;mso-bidi-font-family:Symbol;mso-ansi-language:\r\nRU&quot;&gt;&lt;span style=&quot;font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &amp;quot;Times New Roman&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:14.0pt;mso-bidi-font-size:12.0pt;line-height:107%;\r\nfont-family:&amp;quot;Times New Roman&amp;quot;,serif;mso-ansi-language:RU&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; line-height: 107%; font-family: &amp;quot;Times New Roman&amp;quot;, serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;Н&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; line-height: 107%; font-family: &amp;quot;Times New Roman&amp;quot;, serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;анесите масло, на сухую очищенную кожу\r\nмягкими массажными движениями по направлению снизу верх, уделяя особое внимание\r\nпроблемным зонам. Антицеллюлитное масло идеально подходит для проведения\r\nручного массажа на проблемных зонах. &lt;/span&gt;&lt;b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:14.0pt;mso-bidi-font-size:12.0pt;\r\nline-height:107%;font-family:&amp;quot;Times New Roman&amp;quot;,serif;mso-ansi-language:RU&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/b&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p class=&quot;MsoListParagraphCxSpMiddle&quot; style=&quot;text-align:justify&quot;&gt;&lt;b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:14.0pt;\r\nmso-bidi-font-size:12.0pt;line-height:107%;font-family:&amp;quot;Times New Roman&amp;quot;,serif;\r\nmso-ansi-language:RU&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li style=&quot;text-align: justify; text-indent: -18pt;&quot;&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:\r\n14.0pt;mso-bidi-font-size:12.0pt;line-height:107%;font-family:Symbol;\r\nmso-fareast-font-family:Symbol;mso-bidi-font-family:Symbol;mso-ansi-language:\r\nRU&quot;&gt;&lt;span style=&quot;font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &amp;quot;Times New Roman&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; line-height: 107%; font-family: &amp;quot;Times New Roman&amp;quot;, serif; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;Массажными движениями, наносите крем\r\nутром или вечером на участки, более всего подверженные появлению целлюлита\r\n(бедра, живот, ягодицы). Крем обладает разогревающим эффектом, на месте\r\nнанесения средства образуются покраснения, что является нормальной реакцией\r\nорганизма. &lt;b&gt;Рекомендуется использовать каждый день (утром или вечером), чередуя\r\nс маслом&lt;/b&gt;.&lt;/span&gt;&lt;b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:14.0pt;mso-bidi-font-size:12.0pt;line-height:107%;font-family:\r\n&amp;quot;Times New Roman&amp;quot;,serif;mso-ansi-language:RU&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/b&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p class=&quot;MsoListParagraphCxSpMiddle&quot;&gt;&lt;b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:12.0pt;line-height:107%;font-family:&amp;quot;Times New Roman&amp;quot;,serif;\r\nmso-ansi-language:RU&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;&lt;h1 style=&quot;text-align:justify&quot;&gt;&lt;b&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size:12.0pt;\r\nline-height:107%;font-family:&amp;quot;Times New Roman&amp;quot;,serif;mso-ansi-language:RU&quot;&gt;&lt;u&gt;Схема\r\nежедневного применения комплекса:&lt;/u&gt;&lt;/span&gt;&lt;/b&gt;&lt;/h1&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-right: -11.7pt; margin-left: 36pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&lt;b&gt;1&lt;/b&gt; &lt;b&gt;день&lt;/b&gt; - щетка + масло&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-right: -11.7pt; margin-left: 36pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;span lang=&quot;RU&quot; style=&quot;font-size: 14pt; font-family: &amp;quot;Times New Roman&amp;quot;, serif;&quot;&gt;&lt;b&gt;2 день&lt;/b&gt;