INSERT INTO `product_apts` VALUES (117, 1, 'Результат', '&lt;p style=&quot;outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;90% отметили отличный результат в борьбе с целлюлитом уже после нескольких применений комплекса.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;89% констатировали избавление от «бугорков» и «ямочек» благодаря антицеллюлитному комплексу.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;99% подтвердили, что кожа стала более упругой и эластичной, контуры тела стали более четкие.&lt;/p&gt;&lt;p style=&quot;outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;&quot;&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней&lt;/p&gt;', 4);
