INSERT INTO `product_apts` VALUES (121, 1, 'Результат', '&lt;p&gt;89% согласились, что после применения скраба кожа стала более мягкой и шелковистой.\r\n&lt;/p&gt;&lt;p&gt;90% констатировали заметное уменьшение проявлений целлюлита на коже.&lt;/p&gt;&lt;p&gt;\r\n98% отметили, что после нескольких применений средства кожа стала более подтянутой и упругой.&lt;/p&gt;\r\n\r\n&lt;p&gt;*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
