INSERT INTO `product_apts` VALUES (126, 1, 'Результаты', '<ul style=\"outline: 0px; margin-bottom: 10px; padding-left: 15px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><li style=\"outline: 0px; line-height: 20px !important;\">Устранение шелушений</li><li style=\"outline: 0px; line-height: 20px !important;\">Выравнивание цвета и тона кожи</li><li style=\"outline: 0px; line-height: 20px !important;\">Повышение эластичности и упругости</li></ul><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">89% отметили, что скраб отлично подтягивает кожу и уменьшает целлюлит.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">90% констатировали улучшения состояния кожи уже после нескольких применений средства.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">95% заметили, что кожа стала более мягкой и сияющей.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней</p>', 3);
