INSERT INTO `product_apts` VALUES (141, 1, 'Применение', '<p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><span style=\"outline: 0px; font-weight: 700;\">Шаг 1-</span><span style=\"font-weight: 700;\">очищение и детокс</span></p><p style=\"margin-bottom: 10px; outline: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><p style=\"margin-bottom: 10px; outline: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Скраб соляной для тела Водоросли Глина</p><p style=\"margin-bottom: 10px; outline: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Нанесите скраб массажными движениями на влажную кожу. Смойте теплой водой. Отлично использовать скраб в сауне или бане, нанести предварительно на тело и полежать 10-15 мин.</p><p style=\"margin-bottom: 10px; outline: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Для достижения наилучшего косметического эффекта рекомендуется использовать 2-3 раза в неделю.</p><p><span style=\"outline: 0px; font-weight: 700; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\">Шаг 2 –&nbsp;</span><span style=\"color: rgb(41, 37, 63); font-family: standard; font-size: 16px; font-weight: 700;\">омоложение и питание&nbsp;</span></p><p><span style=\"color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\">Наносите крем на кожу утром и вечером, массажными движениями распределяя средство по всему телу. Аромат апельсина и ванили не оставит вас равнодушными.</span></p><p><span style=\"outline: 0px; font-weight: 700; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\">Шаг 3 – глубокое увлажнение</span></p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><p style=\"outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Массажными движениями нанесите масло на тело. Дождитесь полного впитывания масла.</p>', 1);
INSERT INTO `product_apts` VALUES (141, 1, 'Результаты', '<p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><span style=\"outline: 0px; font-weight: 700;\">Независимые потребительские исследования подтвердили:</span></p><ul style=\"outline: 0px; margin-bottom: 10px; padding-left: 15px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><li style=\"outline: 0px; line-height: 20px !important;\">Кожа становится более упругой, плотной и подтянутой</li><li style=\"outline: 0px; line-height: 20px !important;\">Выравнивание текстуры кожи, улучшение тургора</li><li style=\"outline: 0px; line-height: 20px !important;\">Улучшение контуров тела плюс вывод лишней жидкости</li><li style=\"outline: 0px; line-height: 20px !important;\">Увлажнение и омоложение</li></ul><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">90 % отметили уменьшение «апельсиновой корки» на коже и уменьшение отёчности</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">89 % отметили упругость и гладкость кожи при регулярном применении</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">95 % отметили, что кожа подтянулась, увлажнилась и стала более мягкой</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">*результаты независимых потребительских испытаний 2019 года, группа участников 120 человек, продолжительность 14 дней</p>', 2);
