INSERT INTO `product_apts` VALUES (119, 1, 'Состав', '&lt;h3&gt;Bath Salt Rosemary Lavender&lt;/h3&gt;\r\n\r\n&lt;p&gt;Mediterranean Sea Salt, Lavandula Angustifolia Flower, Laminaria, Lavandula Angustifolia Essential Oil, Clary Sage (Salvia Sclarea) Essential Oil, Rosemary (Rosmarinus Officinalis) Essential Oil, SC-CO2 Extracts (Laminaria Saccharina Extract, Fucus Vesiculosus Extract, Dandelion (Taraxacum officinale) Extract, Urtica Dioica Extract, Acorus Calamus (Sweet Flag) Extract).\r\n&lt;/p&gt;\r\n\r\n&lt;h3&gt;Sea Salt Scrub Rosemary Lavander&lt;/h3&gt;\r\n\r\n&lt;p&gt;Salt, Prunus Amygdalus Dulcis (Sweet Almond) Oil, Cocos Nucifera (Coconut) Oil, Olea Europaea (Olive) Husk Oil, Butyrospermum Parkii (Shea) Butter, Persea Gratissima Oil, Lavandula Angustifolia Essential Oil, Clary Sage (Salvia Sclarea) Essential Oil, Rosemary (Rosmarinus Officinalis) Essential Oil, Lavandula Angustifolia Flower, Tocopherol (mixed), Glyceryl Linoleate, Beta-Sitosterol, Squalene, Tocopheryl Acetate.\r\n&lt;/p&gt;\r\n\r\n&lt;h3&gt;Anti Stress oil&lt;/h3&gt;\r\n\r\n&lt;p&gt;Prunus Amygdalus Dulcis (Sweet Almond) Oil, Prunus Persica (Peach) Kernel Oil, Tocopherol (mixed), Glyceryl Linoleate, Beta-Sitosterol, Squalene, Lavandula Angustifolia Essential Oil, Grapefruit (Citrus paradisi) Essential Oil, Clary Sage (Salvia Sclarea) Essential Oil, Rosemary (Rosmarinus Officinalis) Essential Oil, SC- CO2 Extracts (Chamomilla Recutita (Flower) Extract, Rosa Canina (Rosehip) Fruit Extract, Mint (Mentha Piperita) Extract, Salvia Officinalis Extract), Tocopheryl Acetate. \r\n&lt;/p&gt;', 0);
INSERT INTO `product_apts` VALUES (119, 1, 'Применение', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><span style=\"outline: 0px; font-weight: 700;\">Шаг 1 – расслабление</span></p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Ванна с солью</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Растворите 100-150 г. соли в ванне с теплой водой (36-38°С), принимайте ванну в течение 10-15 минут.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><span style=\"outline: 0px; font-weight: 700;\">Шаг 2-глубокое очищение и детокс</span></p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Массажными движениями нанесите скраб на влажную кожу. Смойте скраб теплой водой.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><span style=\"outline: 0px; font-weight: 700;\">Шаг 3 – смягчение и релакс</span></p><p style=\"outline: 0px; margin-bottom: 0px; padding-bottom: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">После принятия ванны нанесите на кожу масло Антистресс массажными движениями, дождитесь полного впитывания средства. Можно провести процедуру массажа.</p>', 1);
INSERT INTO `product_apts` VALUES (119, 1, 'Результат', '&lt;p&gt;90% отметили, что после использования комплекса кожа стала более мягкой и упругой.\r\n&lt;/p&gt;&lt;p&gt;97% констатировали улучшение настроения и крепкий сон после использования комплекса.\r\n&lt;/p&gt;&lt;p&gt;89% согласились, что комплекс отлично расслабляет тело и снимает нервное напряжение. \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней \r\n&lt;/p&gt;', 2);
