INSERT INTO `product_apts` VALUES (106, 1, 'Применение', 'Нанесите лосьон на кожу, массажными движениями распределите средство по всему телу до полного впитывания. Рекомендуется использовать после принятие душа с гелем Королевский Пачули.', 1);
INSERT INTO `product_apts` VALUES (106, 1, 'Результат', '&lt;p&gt;78% отметили, что кожа стала более гладкая и эластичная.\r\n&lt;/p&gt;&lt;p&gt;\r\n86% согласились, что средство отлично увлажняет кожу.&lt;/p&gt;\r\n&lt;p&gt;87% констатировали, что средство отлично борется с сухостью и шелушениями кожи.  &lt;/p&gt;\r\n\r\n*результаты независимых потребительских испытаний 2018 года, группа участников 130 человек, продолжительность 14 дней\r\n&lt;/p&gt;', 2);
INSERT INTO `product_description` VALUES (50, 1, 'Мицеллярная вода Роза', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Мицеллярная вода содержит комплекс необходимых элементов для тщательного и в тоже время деликатного очищения кожи от макияжа и загрязнений. Благодаря уникальной формуле, которая имеет в составе 8 аминокислот, средство проникает глубоко в кожу, очищает ее и тонизирует.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Содержит экстракт яблони, замедляющий процесс старения, за счет активизации стволовых клеток кожи.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Масло розовых лепестков регулирует работы сальных желез, делая кожу менее жирной, придает ей большую эластичность и увлажненность.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Уникальный водорослевый комплекс Альго+ насыщает кожу минералами и микроэлементами, которые так необходимы коже после долгого дня противостояния негативному влиянию внешней среды.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Мицеллярная вода бережно удаляет макияж лица и глаз, дарит коже ощущение комфорта и легкости.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/eks-yabl.jpg\" data-pagespeed-url-hash=\"501939745\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт яблони<br style=\"outline: 0px;\"></h3>Яблоневый экстракт получают из плодов и листьев дерева. Обладая целым рядом полезных свойств, экстракт яблони хорошо питает и увлажняет кожу, а также делает кожу более стойкой по отношению к негативному влиянию среды, осветляет и тонизирует кожу. Экстракт содержит большой процент растительного глицерина, активируя стволовые клетки кожи, он замедляет процесс старения.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/ef-roza.jpg\" data-pagespeed-url-hash=\"2646498123\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло розы<br style=\"outline: 0px;\"></h3>Масло из лепестков розы является одним из самых дорогих в мире, так как для получения всего одной капли масла нужно собрать около 30 бутонов розы. Свойства масла неоценимы для нашей кожи, так как вещества, содержащиеся в нем, нормализуют работу сальных желез, выравнивают цвет лица, делают кожу более упругой и эластичной, задерживает влагу в клетках кожи.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/8amino.jpg\" data-pagespeed-url-hash=\"344042549\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс 8 аминокислот<br style=\"outline: 0px;\"></h3>Одним из преимуществ аминокислот является небольшой размер молекул, что позволяет им проникать в глубокие слои кожи и участвовать в процессах обновления. Комплекс на основе аминокислот, пирролидонкарбоксилата натрия и бетаина оказывает длительное увлажняющее действие благодаря способности входящих в его состав веществ аккумулировать большое количество молекул воды. Аминокислоты восстанавливают барьерные функции кожи, а также способствуют регенерации тканей и заживлению мелких ранок.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Algo+<br style=\"outline: 0px;\"></h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Мицеллярная вода Роза - &quot;Биоталь&quot;', 'Мицеллярная вода Biothal содержит комплекс необходимых элементов для тщательного и в тоже время деликатного очищения кожи от макияжа и загрязнений.', NULL, NULL, 'Мицеллярная вода содержит комплекс необходимых элементов для тщательного и в тоже время деликатного очищения кожи от макияжа и загрязнений.');
INSERT INTO `product_description` VALUES (51, 1, 'Сыворотка-эликсир Антиэйдж', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Омолаживающая антивозрастная сыворотка Элексир \"Анти-Эйдж\" создана для возвращения коже молодости и свежести. Эта обновляющая сыворотка с гиалуроновой кислотой и пептидным комплексом борется с возрастными изменениями кожи. Улучшает структуру кожи, способствует регенерации эпидермиса, сокращает морщинки, делает кожу гладкой и нежной.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Пептидный комплекс активно восстанавливает волокна коллагена, проникая в глубокие слои эпидермиса. Водорослевой комплекс Альго+ обогащает кожу минералами и микроэлементами, передавая коже свои уникальные способности к регенерации тканей. Доказано клиническими исследованиями разглаживание морщин уже через 2 недели применения сыворотки.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Подарите новую жизнь Вашей коже с Анти-возрастной сывороткой от Biothal.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xpeptidy.jpg.pagespeed.ic._HpgVvJOXA.webp\" data-pagespeed-url-hash=\"2132137476\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Пептидный комплекс</h3>Пептидный комплекс – это натуральный реконструктор матрикса дермы. Он замедляет процессы старения и обладает высокой проникающей способностью в глубокие слои кожи, стимулируя клетки молодости в выработке компонентов внеклеточного матрикса кожи. Укрепление матрикса способствует заживлению ранок на коже и восстановлению волокон коллагена. Также матрикс играет важную роль в сохранении эластичности и упругости кожи.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xkislota.jpg.pagespeed.ic.ch7u87nAfJ.webp\" data-pagespeed-url-hash=\"2876933756\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Гиалуроновая кислота</h3>Гиалуроновая кислота является одним из составляющих нашей кожи, однако условия окружающей среды, плохая экология и стрессы являются причиной потери данного вещества кожей. Поэтому необходимо восстанавливать баланс гиалуроновой кислоты для поддержания водного баланса и обеспечения упругости и эластичности кожи. Данное вещество делает кожу более нежной, бархатистой и замедляет процесс старения.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго+<br style=\"outline: 0px;\"></h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Сыворотка-эликсир Антиэйдж Биоталь', 'Омолаживающая антивозрастная сыворотка Элексир &quot;Анти-Эйдж&quot;  Биоталь создана для возвращения коже молодости и свежести. Эта обновляющая сыворотка с гиалуроновой кислотой и пептидным комплексом борется с возрастными изменениями кожи.', NULL, NULL, 'Омолаживающая антивозрастная сыворотка Элексир Анти Эйдж создана для возвращения коже молодости и свежести. Эта обновляющая сыворотка с гиалуроновой кислотой и пептидным комплексом борется с возрастными изменениями кожи.');
INSERT INTO `product_description` VALUES (57, 1, 'Крем для тела Омолаживающий и Питательный', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Питательный омолаживающий крем для тела на основе масел дерева ши и вечерней примулы бережно защищает кожу от внешних воздействий и эффективно увлажняет сухую, увядшую кожу. Сбалансированная формула питательных масел примулы, апельсина и подсолнечника активно замедляет процессы старения, насыщая ослабленную кожу необходимыми веществами.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Благодаря богатому составу крем омолаживает кожу, придавая ей упругость и интенсивное увлажние. Нежная текстура крема не утяжеляет кожу, обеспечивая быстрое впитывание. Линолевая кислота, входящая в состав масла вечерней примулы, питает сухую тусклую кожу, возвращая ей молодость и красоту. Масло семян подсолнечника оказывает успокаивающее действие, а протеины пшеницы производят на кожу лифтинг-эффект, подтягивая её и делая более упругой. Уникальный комплекс Альго+, в состав которого входят разнообразные водоросли и морская соль, благотворно влияет на слой эпидермиса благодаря активным веществам, проникающим в глубокие слои кожи.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo_vecherney_primuly.jpg\" data-pagespeed-url-hash=\"945457876\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло примулы вечерней<br style=\"outline: 0px;\"></h3>Масло примулы, раскрывающей свои лепестки после полуночи, получают из семян способом холодного отжима. Такое масло высоко ценится, так как собирать семена примулы очень сложно. Масло содержит много жирных кислот, среди которых основное место занимает линолевая. Усиленная альфа- и гамма- компонентами она хорошо питает кожу и защищает её от ультрафиолетовых лучей. Кроме того, масло примулы имеет антиоксидантное, антивозрастное и заживляющее действие. Идеально подходит для сухой, чувствительной и стареющей кожи.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/apelsin.jpg\" data-pagespeed-url-hash=\"4217814105\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Апельсиновое масло<br style=\"outline: 0px;\"></h3>Апельсиновое масло производят из кожуры или из плодов апельсинового дерева. Уникальный состав масла является настоящей находкой для ухода за телом. Масло апельсина имеет омолаживающий эффект, отлично восстанавливает тонус и упругость кожных покровов. Является незаменимым средством в борьбе с целлюлитом. Широко применяется для сухой, увядающей и тусклой кожи.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-pods.jpg.pagespeed.ic.CZv8UqAQFh.webp\" data-pagespeed-url-hash=\"3789374328\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло семян подсолнечника<br style=\"outline: 0px;\"></h3>Масло из семян подсолнечника богато витамином Е, имеющим отличные антиоксидантные свойства, замедляющие процесс старения кожи. Масло нежно обволакивает кожу тонкой пленкой, которая не утяжеляет и не стягивает ее, при этом прекрасно увлажняет и способствует ускоренной регенерации клеток. Отлично раскрывает свои петельные свойства на сухой и увядшей коже, увлажняя ее и восстанавливая изнутри.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/protein.jpg\" data-pagespeed-url-hash=\"2107569866\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Пшеничный протеин</h3>Пшеница богата протеинами, которые состоят из высокомолекулярных белков глютена, глиадина и лифтина. Глютен является природным антиоксидантом, глиадин является стимулятором синтеза коллагена, а лифтин создаёт на поверхности кожи смягчающую защитную пленку. Пшеничные протеины содержат аминокислоты, сходные по составу с теми, что содержатся в нашей коже, поэтому протеины хорошо усваиватся, обновляют и восстанавливают кожу.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-shi.jpg.pagespeed.ic.s_Tq948yRX.webp\" data-pagespeed-url-hash=\"477139764\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло ши<br style=\"outline: 0px;\"></h3>Извлечение масла из плодов дерева ши является длительным процессом, так как это делается вручную, чтобы бережно сохранить полезные свойства и нежный ореховый аромат продукта. Масло ши смягчает кожу и защищает её от вредного воздействия среды, при этом, не закупоривая поры. Входящие в состав масла каристеролы способствуют регенерации клеток, а жирные кислоты, которыми богато масло, питают и увлажняют кожу. Уникальная формула масла ши регенерирует клетки кожи и оказывает на них антивозрастной эффект.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Натуральный Крем для тела Биоталь Омолаживающий и Питательный', 'Питательный омолаживающий крем для тела Biothal на основе масел дерева ши и вечерней примулы бережно защищает кожу от внешних воздействий и эффективно увлажняет сухую, увядшую кожу.', NULL, NULL, 'Питательный омолаживающий крем для тела на основе масел дерева ши и вечерней примулы бережно защищает кожу от внешних воздействий и эффективно увлажняет сухую, увядшую кожу.');
INSERT INTO `product_description` VALUES (59, 1, 'Детокс-пилинг скраб для лица Петитгрейн', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Пилинг скраб для лица с богатой нежной текстурой призван освободить Вашу кожу от тусклости и вялости. Благодаря его уникальной формуле и активным компонентам кожа гладкая и сияющая уже после первого применения. Эффект скраба достигается благодаря вулканической пемзе, а энзимы граната обеспечивают активный пилинг кожи.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Детокс пилинг скраб бережно заботится о коже, отшелушивая омертвевшие частицы эпидермиса и активизируя рост новых здоровых клеток. Эфирное масло петитгрейна замедляет процессы старения, насыщает кожу влагой и способствует выработке коллагена. Приятный аромат масла дарит расслабление и восстанавливает гармонию во всем организме. Частицы вулканической пемзы нежно массируют кожу, обновляя слой эпидермиса и улучшая кровоснабжение. Кожа получает все необходимые витамины благодаря добавлению энзимов граната и миндального масла.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Детокст пилинг скраб подходит для всех типов кожи.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-petigreyda.jpg\" data-pagespeed-url-hash=\"2328868904\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло петитгрейна&nbsp;<br style=\"outline: 0px;\"></h3>Масло петитгрейна - это цитрусовое масло, полученное из побегов и листьев апельсинового дерева. Масло имеет приятный аромат, который восстанавливает гармонию внутренних и физических сил и является источником легкости. Масло замедляет процессы старения: возрождает кожу от сухости, дряблости и провисаний, также способствует выработке коллагена в коже. Сохраняя влагу в клетках кожи, способствует нормализации водного баланса и насыщает кожу витаминами и органическими кислотами.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/pemza.jpg\" data-pagespeed-url-hash=\"610246734\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Вулканическая пемза<br style=\"outline: 0px;\"></h3>Натуральная пемза это вулканическая порода, которая десятки лет назад имела вид жидкой пены. Она содержала множество пузырьков газа, поэтому сейчас пемза имеет пористую структуру. Пемза в составе косметики работает как эксфолиант – отшелушивающее вещество, она размягчает загрубевшие участки кожи, выравнивает рельеф и стимулирует обновление клеточного слоя.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br style=\"outline: 0px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/granat.jpg\" data-pagespeed-url-hash=\"601076362\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт граната<br style=\"outline: 0px;\"></h3>Плоды граната содержат пятнадцать аминокислот и такие микроэлементы как кальций, йод, железо, витамины С, Р, В6 и В12. Компоненты, входящие в состав граната снимают раздражения кожи, активизируют выработку коллагена и улучшают кровообращение. Экстракт граната обладает большим количеством антиоксидантов, которые выводят из кожи токсины и шлаки. Экстракт граната содержит особые вещества энзимы, благодаря которым достигается эффект пилинга.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-mindal.jpg\" data-pagespeed-url-hash=\"279319911\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Миндальное масло</h3>Для получения миндального масла используются два вида миндаля: горький и сладкий. Миндальное масло является одним из базовых масел, на основе которых создаются косметические средства. Обилие витамина Е замедляет процесс старения клеток кожи, а витамин F нормализует работу сальных желёз и способствует очищению и сужению пор. Витамин А смягчает и увлажняет кожу. Миндальное масло благотворно влияет на кожу вокруг глаз, устраняя мимические морщинки и осветляя круги усталости.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br style=\"outline: 0px;\"></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго+<br style=\"outline: 0px;\"></h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/allantoin.jpg\" data-pagespeed-url-hash=\"2180348513\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Аллантоин<br style=\"outline: 0px;\"></h3>Аллантоин – это вещество природного происхождения, содержащееся в улиточном секрете и благотворно влияющее на кожу. Аллантоин смягчает кожу, интенсивно увлажняет, способствует сужению пор и нормализации работы сальных желез, обладает противовоспалительными свойствами. Вещество полностью гиппоаллергенно, а его активность проявляется даже в небольшой концентрации. Исследования доказали, что аллантоин по своим антиоксидантным свойствам не уступает витамину С. Он снижает воздействие свободных радикалов на клеточную мембрану, продлевая таким образом жизнь клетки.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/kaprilik.jpg\" data-pagespeed-url-hash=\"886411804\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Каприлик триглицерид<br style=\"outline: 0px;\"></h3>Каприлик триглицерид создают из кокосового масла и глицерина, получая жидкость с мягкой, бархатистой текстурой, широко применимую в косметических целях. Это отличное смягчающее средство, которое создает на поверхности кожи защитную пленку, предотвращая сухость и загрязнения.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Детокс-пилинг скраб для лица Петитгрейн - &quot;Биоталь&quot;', 'Натуральный Пилинг скраб Biothal для лица с богатой нежной текстурой призван освободить Вашу кожу от тусклости и вялости. Благодаря его уникальной формуле и активным компонентам кожа гладкая и сияющая уже после первого применения.', NULL, NULL, 'Пилинг скраб для лица с богатой нежной текстурой призван освободить Вашу кожу от тусклости и вялости. Благодаря его уникальной формуле и активным компонентам кожа гладкая и сияющая уже после первого применения.');
INSERT INTO `product_description` VALUES (62, 1, 'Соль для ванн Розмарин-Лаванда', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Расслабляющая Соль для ванн розмарин лаванда дарит телу приятную легкость, успокаивает кожу и окутывает Вас нежным ароматом природных масел. Натуральная морская соль средиземноморья, которая используется как основа средства, насыщает кожу минералами и микроэлементами, которые она вбирает в себя из океанских вод. Соль стимулирует регенерацию клеток кожи, успокаивает нервы и выводит шлаки и токсины из организма. Эфирное масло шалфея регулирует работу сальных желез, создает на коже защитный барьер, который предохраняет ее от ультрафиолетовых лучей и свободных радикалов. Экстракт розмарина уменьшает поры, разглаживает кожу, устраняет кожные дефекты. Благодаря маслу лаванды, Соль для ванны имеет нежный цветочный аромат и дарит телу покой и расслабление.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/sea-salt.jpg\" data-pagespeed-url-hash=\"302412979\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Морская соль Средиземноморья<br style=\"outline: 0px;\"></h3>В морской соли содержится множество минералов и микроэлементов необходимых нашему организму и в особенности коже. Морская соль содержит такие вещества, как йод, кальций, магний, железо, калий, бром и другие. Морская соль стимулирует регенерацию тканей, успокаивает нервы и выводит вредные вещества из тканей организма. Она эффективно и бережно отшелушивает омертвевшие клетки эпидермиса, обновляя кожу. Морская соль способствует осветлению кожи и выравниванию ее рельефа.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/efirnoe-maslo-shalfeya.jpg\" data-pagespeed-url-hash=\"3790775860\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло шалфея</h3>Эфирное масло шалфея получают из листьев растения способом дистилляции. Свойства шалфея благотворно влияют на жирную, комбинированную и увядающую кожу. В составе шалфея находятся карнозная и урсоловая кислоты, обладающие противомикробным и противовоспалительным действием. Эти кислоты защищают кожу от вредных воздействий и ультрафиолетовых лучей. Вещества флавноиды способны предохранять клетки от повреждений.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/eks-rozm.jpg\" data-pagespeed-url-hash=\"1641811147\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт розмарина</h3>Вечнозеленое растение розмарин в древние времена считалось поистине волшебным, благодаря своим целительным свойствам. Оно обладает антисептическим действием, а так же способностью сокращать и стягивать поры.&nbsp;Экстракт розмарина разглаживает кожу, придаёт ей большую эластичность, убирает рубцы и пигментные пятна.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-lavand.jpg.pagespeed.ic.xMQrK0eIJr.webp\" data-pagespeed-url-hash=\"1252365848\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло лаванды</h3>Лаванда – вечнозеленое растение, обладающее нежным ароматом и уникальными свойствами, широко известными в косметологии. Эфирное масло лаванды дарит коже лёгкость, выходит шлаки и токсины, очищает ее от омертвевших частиц. Бережное воздействие масла позволяет использовать его для ухода чувствительной и проблемной кожей.</div></div>', NULL, 'Соль для ванн Розмарин-Лаванда - &quot;Биоталь&quot;', 'Расслабляющая Соль для ванн Biothal - Розмарин-Лаванда дарит телу приятную легкость, успокаивает кожу и окутывает Вас нежным ароматом природных масел.', NULL, NULL, 'Расслабляющая Соль для ванн розмарин лаванда дарит телу приятную легкость, успокаивает кожу и окутывает Вас нежным ароматом природных масел.');
INSERT INTO `product_description` VALUES (63, 1, 'Соль для ванн Дикая Роза', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Соль для ванн дикая роза создана специально для того, чтобы дарить наслаждение и расслабление Вашему телу. Чувственный аромат дикой розы окутает Вас с ног до головы, располагая организм к расслаблению и умиротворению. Активные компоненты натуральной морской соли улучшают состояние кожи, и благотворно влияют на общее состояние организма. Содержащиеся в морской соли калий и натрий помогают питательным веществам проникать в клетки кожи. Кальций нормализует работу нервной системы и улучшает кровообращение. Бром в составе морской соли оказывает успокаивающее действие на организм и улучшает состояние кожи. Ванны с солью Дикая роза выводят из кожи шлаки и токсины, делая ее более мягкой и нежной.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Подарите себе эстетическое удовольствие, а своему организму расслабление после долгого дня с солью для ванн Дикая Роза.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xsea-salt.jpg.pagespeed.ic.AsSVujT234.webp\" data-pagespeed-url-hash=\"302412979\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Морская соль Средиземноморья<br style=\"outline: 0px;\"></h3>В морской соли содержится множество минералов и микроэлементов необходимых нашему организму и в особенности коже. Морская соль содержит такие вещества, как йод, кальций, магний, железо, калий, бром и другие. Морская соль стимулирует регенерацию тканей, успокаивает нервы и выводит вредные вещества из тканей организма. Она эффективно и бережно отшелушивает омертвевшие клетки эпидермиса, обновляя кожу. Морская соль способствует осветлению кожи и выравниванию ее рельефа.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xroza.jpg.pagespeed.ic.KOaz641dby.webp\" data-pagespeed-url-hash=\"2627804815\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт марокканской розы<br style=\"outline: 0px;\"></h3>Марокканская роза – это цветок, обладающий самым сильным и притягательным ароматом из всех разновидностей роз. Экстракт марокканской розы обладает противовоспалительными, охлаждающими, успокаивающими свойствами. Экстракт тонизирует, подтягивает и омолаживает кожу, делая ее гладкой и упругой. Благодаря высокому уровню жирных кислот, содержащихся в марокканской розе, она отлично восстанавливает защитные функции кожи, замедляет процесс старения клеток, подтягивает и тонизирует кожу.</div></div>', NULL, 'Соль для ванн Биоталь Дикая Роза', 'Соль для ванн &quot;Биоталь&quot; дикая роза создана специально для того, чтобы дарить наслаждение и расслабление Вашему телу. Чувственный аромат дикой розы окутает Вас с ног до головы, располагая организм к расслаблению и умиротворению.', NULL, NULL, 'Соль для ванн дикая роза создана специально для того, чтобы дарить наслаждение и расслабление Вашему телу. Чувственный аромат дикой розы окутает Вас с ног до головы, располагая организм к расслаблению и умиротворению.');
INSERT INTO `product_description` VALUES (64, 1, 'Масло Антицеллюлит', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Масло Антицеллюлит интенсивно воздействует на кожу, благодаря активным компонентам, проникающим в глубокие слои эпидермиса. Кожа становиться подтянутой и эластичной, «ямочки» и «бугорки» разглаживаются, тон кожи выравнивается. Масло зеленых кофейных зерен улучшает кровоснабжение клеток, питает и обновляет клетки кожи.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Масло Антицеллюлит создано на основе комплекса природных масел и экстрактов, которые оказывают на кожу омолаживающее действие, тонизируя ее и стимулируя активную регенерацию клеток. Эфирное масло можжевельника улучшает клеточный метаболизм, масло грейпфрута активизирует выработку коллагена и эластина. Мощный антиоксидант, входящий в состав средства отлично проникает в глубокие слои эпидермиса, выравнивая поверхность кожи, заживляя ранки и повреждения. Благодаря оливковому маслу, кожа насыщается комплексом витаминов, становится нежной и бархатистой. Экстракт кофе усиливает липолиз, оказывая корректирующее действие на жировые ткани, способствуя уменьшению видимых проявлений целлюлита. Масло персиковых косточек выравнивает кожный рельеф, а масло лимона сужает и очищает поры, нормализуя работу сальных желез. Масло помогает избавиться от омертвевших участков, выравнивает цвет кожи, улучшает ее эластичность. В сочетании с регулярными массажами проблемных зон, средство эффективно борется с целлюлитом и другими кожными дефектами, возвращая ей упругость и сияние.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-mindal.jpg.pagespeed.ic.zubr6g3R4g.webp\" data-pagespeed-url-hash=\"279319911\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Миндальное масло<br style=\"outline: 0px;\"></h3>Для получения миндального масла используются два вида миндаля: горький и сладкий. Миндальное масло является одним из базовых масел, на основе которых создаются косметические средства. Масло отлично подходит для ухода за нежной, чувствительной кожей. Проникая в глубокие слои эпидермиса, оно активизирует кровообращение. Кожа становится более гладкой и подтянутой, так как масло способствует выводу лишней жидкости и шлаков.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xeks-kofe-ar.jpg.pagespeed.ic.LWTS_f88ML.webp\" data-pagespeed-url-hash=\"837690122\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт кофе арабика</h3>Экстракт зерен кофе сорта арабика имеет множество питательных веществ, благотворно влияющих на кожу. Он обладает антиоксидантными и тонизирующими свойствами, уменьшает возрастные изменения кожи, разглаживает морщинки, делает кожу более эластичной. Усиливает липолиз, оказывая корректирующее действие на жировые ткани, способствуя уменьшению видимых проявлений целлюлита.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/eks-kasht.jpg\" data-pagespeed-url-hash=\"3335357520\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт каштана<br style=\"outline: 0px;\"></h3>Экстракт плодов каштана имеет противовоспалительное действие, избавляет тело от отеков, выводя избыточную жидкость из организма. Экстракт каштана обладает выраженным антицеллюлитным и дренирующим действием. Оказывает тонизирующее действие на кожу, улучшает микроциркуляцию, способствует выведению продуктов метаболизма клеток, обладает антиоксидантной активностью.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xtokoferol.jpg.pagespeed.ic.ySl3t0TEtZ.webp\" data-pagespeed-url-hash=\"1912770404\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Токоферол</h3>Токоферол – это мощный антиаксидант, который проникая в глубокие слои эпидермиса гидролизуется в витамин Е. Защищает поверхность кожи от негативного воздействия солнечных лучей, препятствует разрушающему действию свободных радикалов. Выравнивает поверхность кожи, заживляет ранки и повреждения.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/ef-mogh.jpg\" data-pagespeed-url-hash=\"416284098\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло можжевельника</h3>Масло можжевельника получают из ягод этого реликтового растения, с древности славившегося своими антисептическими и очищающими свойствами. Масло отлично тонизирует кожу, способствуя её обновлению, и увеличивает клеточный метаболизм. Активные компоненты, содержащиеся в ягодах растения, выравнивают цвет кожи, повышают её упругость и омолаживают её изнутри. Эффективно борется с целлюлитом, а также используется для его профилактики.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/gr.png\" data-pagespeed-url-hash=\"1680844122\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло грейпфрута</h3>Грейпфрут – это вечнозеленое растение произрастающее исключительно в тропическом климате. Масло грейпфрута является отличным тонизирующим средством для кожи, оно восстанавливает водный баланс, снимает отеки и помогает избавляться от целлюлита. Масло грейпфрута способствует сохранению упругости и гладкости кожи, тормозя действие фермента эластазы, разрушающего волокна коллагена и эластина.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/ef-limona.jpg\" data-pagespeed-url-hash=\"3691030173\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло лимона</h3>Лимонное масло получают путем холодного отжима кожуры плодов, мдля получения 1 кг эфирного масла потребуется более 3000 плодов цитрусовых. Масло лимона способствует сужению и очищению пор, нормализуя работу сальных желез. Масло помогает избавиться от омертвевших участков, выравнивает цвет кожи, улучшает ее эластичность. Обладает противовоспалительным и антисептическим действием. Эфирное масло лимона содержит большое количество монотерпенов, которые обеспечивают болеутоляющее, согревающее кожу действие.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-olivka.jpg\" data-pagespeed-url-hash=\"1965914910\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Оливковое масло</h3>Оливковое масло первого отжима считается самым полезным и богатым витаминами из разнообразных оливковых масел. Оливковое масло богато скваланом,&nbsp;веществом, которое интенсивно увлажняет кожу. Также, оливковое масло имеет в своем составе много кальция, железа, меди, витамины&nbsp;А, D&nbsp;и&nbsp;Е. Содержащиеся в масле витамины А и D участвуют в обновлении эпидермиса, отшелушивают ороговевшие участки кожи и защищают от загрязнений окружающей среды. Витамин A делает кожу увлажненной и эластичной.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/136-1_olej-z-psenicnych-klicku.jpg\" data-pagespeed-url-hash=\"1960112840\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло зародышей пшеницы</h3>Для кожи масло зародышей пшеницы является источником полезных витаминов и микроэлементов. Ростки пшеницы содержат уникальный комплекс биологически активных соединений, питательных веществ и элементов, который благотворно влияю на состояние кожи, омолаживая и осветляя ее. В состав масла входят аминокислоты, жирные кислоты, витамины групп В, Е, А и D, а также такие микроэлементы, как цинк, селен, фосфор, марганец, железо и другие. Масло ростков пшеницы прекрасно снимает воспаления, борется с морщинами и тонизирует кожу.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/persikovoe-maslo-1.jpg\" data-pagespeed-url-hash=\"1041930473\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло персиковых косточек</h3>Масло персиковых косточек получают методом холодного прессования ядер плода персика. Богатый состав масла поддерживает упругость и эластичность кожи, разглаживает морщины и выравнивает кожный рельеф. Полезно для чувствительной кожи, благодаря содержания витамина Е, который обладает антиоксидантным действием, витамина А, который поддерживает целостность клеток кожи, витамина В15, который, в свою очередь, обладает высокой биологической активностью.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Натуральное Масло Антицеллюлит Biothal', 'Масло Антицеллюлит Biothal интенсивно воздействует на кожу, благодаря активным компонентам, проникающим в глубокие слои эпидермиса. Кожа становиться подтянутой и эластичной, «ямочки» и «бугорки» разглаживаются, тон кожи выравнивается.', NULL, NULL, 'Масло Антицеллюлит интенсивно воздействует на кожу, благодаря активным компонентам, проникающим в глубокие слои эпидермиса. Кожа становиться подтянутой и эластичной, «ямочки» и «бугорки» разглаживаются, тон кожи выравнивается.');
INSERT INTO `product_description` VALUES (65, 1, 'Масло кокосовое', '&lt;p&gt;100% натуральное нерафинированное кокосовое масло, сохранившее все свои природные свойства в первозданно виде. Благодаря уникальной технологии щадящего холодного прессования, кокосовое масло полностью сохраняет свой богатый состав, также средство не имеет посторонних примесей. Кокосовое масло является незаменимым средством для здоровья кожи, волос и общего состояния организма. Оно возрождает молодость и красоту кожи, подтягивая морщинки. Кожа становиться мягкой и увлажненной. В масле содержится около 50% лауриновой кислоты, которая в организме человека превращается в монолаурин – вещество активно борющееся с вирусами. Также лауриновая кислота способствует ускорению заживлений повреждений кожного покрова и активизирует регенерацию кожи. Каприловая кислота способствует восстановлению кислотно-щелочного баланса кожи и улучшает насыщение кожи кислородом. Кокосовое масло отлично подходит для ежедневного ухода за сухой, раздраженной, шелушащейся, воспаленной, огрубевшей, зрелой или увядающей кожи тела. Масло быстро впитывается, но не утяжеляя кожу и не создавая масляную пленку на поверхности, питает и смягчает кожу. Cпособствует сохранению кожей влаги и восстановлению защитных функций эпидермиса, предохраняет кожу от высыхания и шелушения. Тонизирует кожу, придает ей эластичность и упругость, способствует разглаживанию мелких морщин, препятствует появлению растяжек на коже. В составе кокосового масла присутствуют компоненты, стимулирующие естественную выработку коллагена и эластина.&lt;/p&gt;&lt;p&gt;\r\n&lt;/p&gt;&lt;div class=\"row ing\"&gt;\r\n    &lt;h2 style=\" text-align: center ;\"&gt;&lt;/h2&gt;&lt;p style=\"text-align: center;\"&gt;&lt;br&gt;&lt;/p&gt;\r\n    &lt;div class=\"col-sm-1\"&gt;\r\n        &lt;img src=\"/image/catalog/111/cocos_1.jpg\" style=\"width: 100%;\"&gt;\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-11\"&gt;\r\n        &lt;h3&gt;Нерафинированное кокосовое масло&lt;br&gt;&lt;/h3&gt;\r\nДля получения кокосового масла необходимо дождаться, когда кокос созреет до необходимого состояния. Масло получают из мякоти кокоса, которую спрессовывают, чтобы отделить масло. Продукт обладает твёрдой консистенцией, белым цветом, нежно-сладким ароматом и приятным ореховым вкусом. В состав кокосового масла входят ненасыщенные жирные кислоты, фитостеролы, витамины, минералы и гиалуроновая кислота. Масло заметно улучшает состояния кожи и волос. Оно великолепно впитывается в кожу любого типа, увлажняя, смягчая, восстанавливая и улучшая эластичность  кожи. Разглаживает морщинки и защищает от вредоносных влияний окружающей среды.\r\n    &lt;/div&gt;\r\n&lt;/div&gt;&lt;p&gt;&lt;/p&gt;', NULL, 'Масло кокосовое Биоталь', 'Натруальное Кокосовое масло Biothal является незаменимым средством для здоровья кожи, волос и общего состояния организма. Оно возрождает молодость и красоту кожи, подтягивая морщинки. Кожа становиться мягкой и увлажненной.', NULL, NULL, 'Кокосовое масло является незаменимым средством для здоровья кожи, волос и общего состояния организма. Оно возрождает молодость и красоту кожи, подтягивая морщинки. Кожа становиться мягкой и увлажненной.');
INSERT INTO `product_description` VALUES (67, 1, 'Сыворотка-эликсир для кожи вокруг глаз', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Сыворотка Эликсир бережно ухаживает за нежной кожей вокруг глаз, убирает темные круги и разглаживает морщинки. Клинически доказано, уникальный состав сыворотки оказывает тонизирующее действие на кожу, способствуя стимуляции синтеза коллагена.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Сок алоэ в составе средства смягчает кожу, повышает ее эластичность, увлажняет и успокаивает. Витамин В3 ускоряет регенерацию клеток кожи и усиливает барьерные функции. Уникальный водорослевый комплекс Альго+ насыщает кожу активными минералами, улучшая микроциркуляцию крови и активизируя регенерацию клеток. Сыворотка обладает витаминной активностью, проникая в глубокие слои эпидермиса, она оказывает увлажняющее действие, создавая на поверхности кожи защитную пленку. Снимает отёчность, сокращает мелкие морщинки. Делает кожу гладкой и нежной.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/6608_pub.jpg\" data-pagespeed-url-hash=\"3239079049\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Кремний</h3>Диоксид кремния выравнивает кожу и устраняет различные дефекты. Он впитывает влагу и удерживает ее в клетках, контролируя тем самым выделения жира на коже, делая ее матовой. Кремний в составе косметического средства отлично сглаживает морщины. Обладая абсорбирующим действием, дольше сохраняет макияж на лице. Диоксид кремния способен очищать кожу от омертвевших частиц. Кремний хорошо впитывает секрецию сальных желез, потовые выделения.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/kislota.jpg\" data-pagespeed-url-hash=\"2876933756\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Гиалуроновая кислота<br style=\"outline: 0px;\"></h3>Гиалуроновая кислота является одним из составляющих нашей кожи, однако условия окружающей среды, плохая экология и стрессы являются причиной потери данного вещества кожей. Поэтому необходимо восстанавливать баланс гиалуроновой кислоты для поддержания водного баланса и обеспечения упругости и эластичности кожи. Данное вещество делает кожу более нежной, бархатистой и замедляет процесс старения.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго +</h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/sok-aloe.jpg\" data-pagespeed-url-hash=\"2793078866\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Сок алоэ вера</h3>Алоэ вера обладает целым комплексом биологически активных веществ, которые активно борются с бактериями и заживляют ранки. Сок алоэ вера максимально безопасен и подходит даже людям с чувствительной, склонной к аллергиям кожей. Алоэ препятствует снижению синтеза коллагена и эластина, которое вызвано негативным воздействием факторов внешней среды. Наличие в составе растения гликопротеинов повышает способность кожи к восстановлению и уменьшению воспалений. Алоэ отлично восстанавливает поврежденные клетки кожи и, проникая в глубокие слои эпидермиса, эффективно увлажняет ее.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/vit-b.png\" data-pagespeed-url-hash=\"4093975321\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Витамин B</h3>Водорастворимый витамин В3 ускоряет регенерацию клеток кожи, улучшает ее эластичность и усиливает барьерные функции. Он эффективно выравнивает рельеф кожи и осветляет пигментные пятна. Способствует стимуляции синтеза коллагена и помогает снизить потерю влаги кожей, а также увлажняет, сужает поры и успокаивает.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/vit-b5.jpg\" data-pagespeed-url-hash=\"1352913130\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Витамин B5</h3>Витамин B5 используется в качестве целебного средства, полезного при нарушении метаболизма, аллергиях, кожных заболеваниях. Компонент обладает смягчающим действием. Стимулирует производство кортикостероидов и улучшает процессы регенерации. Вещество активизирует метаболические процессы, защищает кожу вредных воздействий, уменьшения раздражения, снижает вероятность аллергических реакций.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/vit-c.png\" data-pagespeed-url-hash=\"93507946\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Витамин С</h3>Витамин С оказывает противовоспалительное действие на эпидермис, а также активно борется с бактериями, вызывающими акне. Компонент защищает кожный покров от ультрафиолетовых лучей. Кроме того, он стимулирует синтез коллагена в клетках кожи. Уменьшает пигментные пятна и делает кожу светлее.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/vit-b6.jpg\" data-pagespeed-url-hash=\"1647413051\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Витамин B6</h3>Витамин B6 регулирует себовыделение, борется с проявлениями акне и другими кожными заболеваниями. Витамин улучшает обмен веществ и благотворно влияет на состояние кожи, делая ее более мягкой и эластичной.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Сыворотка-эликсир для кожи вокруг глаз Биоталь', 'Сыворотка Эликсир Biothal бережно ухаживает за нежной кожей вокруг глаз, убирает темные круги и разглаживает морщинки. Клинически доказано, уникальный состав сыворотки оказывает тонизирующее действие на кожу, способствуя стимуляции синтеза коллагена.', NULL, NULL, 'Сыворотка Эликсир бережно ухаживает за нежной кожей вокруг глаз, убирает темные круги и разглаживает морщинки. Клинически доказано, уникальный состав сыворотки оказывает тонизирующее действие на кожу, способствуя стимуляции синтеза коллагена.');
INSERT INTO `product_description` VALUES (69, 1, 'Скраб кофейный с маслами', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">100% натуральный скраб для тела, созданный на основе средиземноморской соли, молодых кофейных зерен и комплекса натуральных масел отлично тонизирует и омолаживает кожу. Скраб кофейный с маслами подходит для ухода за кожей любого типа и может стать базовым средством по уходу за телом.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Молодые зерна кофе «Робуста» обладают большим содержанием кофеина, который улучшает кровоснабжение клеток, питает и обновляет их. Кофеин также усиливает липолиз, оказывая корректирующее действие на жировые ткани, способствуя уменьшению видимых проявлений целлюлита. Морская соль питает кожу минералами и микроэлементами, выводя из клеток вредные вещества и загрязнения. Миндальное масло замедляет процесс старения клеток кожи, очищает и сужает поры. Благодаря маслу авокадо, питательные вещества проникают в глубокие слои кожи, интенсивно питая ее. Кофе делает кожу мягкой и эластичной и благотворно влияет на работу кровеносной системы. Скраб отлично борется с целлюлитом и растяжками на коже, благодаря уникальным свойствам мандаринового масла. Масло жожоба, в свою очередь, делает кожу упругой и подтянутой. Продукт подходит для всех типов кожи.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/ef-mandarin_01.jpg\" data-pagespeed-url-hash=\"3532932355\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло мандарина</h3>Мандариновое масло получают из кожуры зрелых плодов методом холодного отжима. Оно обладает успокаивающим действием, благотворно влияет на сон, снимает утомление и раздражительность. Масло отлично стимулирует нервную систему, обладает комплексом освежающих, тонизирующих, выравнивающих свойств. Также, мандариновое масло известно своими антицеллюлитными свойствами, оно препятствует отложению жировых прослоек и возникновению растяжек. Подходит для ухода за жирной и увядающей кожей.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xsea-salt.jpg.pagespeed.ic.AsSVujT234.webp\" data-pagespeed-url-hash=\"302412979\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Соль</h3>В морской соли содержится множество минералов и микроэлементов необходимых нашему организму и в особенности коже. Морская соль содержит такие вещества, как йод, кальций, магний, железо, калий, бром и другие. Морская соль стимулирует регенерацию тканей, успокаивает нервы и выводит вредные вещества из тканей организма. Она эффективно и бережно отшелушивает омертвевшие клетки эпидермиса, обновляя кожу. Морская соль способствует осветлению кожи и выравниванию ее рельефа.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-mindal.jpg.pagespeed.ic.zubr6g3R4g.webp\" data-pagespeed-url-hash=\"279319911\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Миндальное масло<br style=\"outline: 0px;\"></h3>Для получения миндального масла используются два вида миндаля: горький и сладкий. Миндальное масло является одним из базовых масел, на основе которых создаются косметические средства. Масло отлично подходит для ухода за нежной, чувствительной кожей. Проникая в глубокие слои эпидермиса, оно активизирует кровообращение. Кожа становится более гладкой и подтянутой, так как масло способствует выводу лишней жидкости и шлаков.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-avocado.jpg.pagespeed.ic.LucuDBT7v7.webp\" data-pagespeed-url-hash=\"2808404183\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло авокадо</h3>Масло авокадо легко проникает в кожу, благодаря его сходному составу с комплексом жирных кислот, помогая ей восстанавливать естественное здоровое состояние. Масло авокадо устраняет шелушения, воспаления, замедляет процессы старения. Благодаря большому количеству витамина А, содержащемуся в авокадо, кожа дольше сохраняет молодость и сияние. Содержание в масле авокадо вещества авокутина снижает жирность кожи на треть через неделю с начала применения.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xtokoferol.jpg.pagespeed.ic.ySl3t0TEtZ.webp\" data-pagespeed-url-hash=\"1912770404\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Токоферол</h3>Токоферол – это мощный антиаксидант, который проникая в глубокие слои эпидермиса гидролизуется в витамин Е. Защищает поверхность кожи от негативного воздействия солнечных лучей, препятствует разрушающему действию свободных радикалов. Выравнивает поверхность кожи, заживляет ранки и повреждения.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-jojoba.jpg\" data-pagespeed-url-hash=\"1275919139\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло жожоба</h3>Масло жожоба является одним из самых полезных по своим свойствам масел для красоты кожи и волос. В составе масла жожоба содержатся аминокислоты и протеин. Они возвращают коже молодость и здоровый вид. Масло жожоба делает кожу упругой и заживляет повреждения. Благодаря нежной текстуре масла, оно хорошо впитываются в кожу, проникая в ее глубокие слои и насыщая клетки питательными веществами.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/coffee-12.jpg\" data-pagespeed-url-hash=\"17350217\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Молотый кофе</h3>Кофе сорта «Робуста» произростает в лесах Африки и Азии и отличается своей природной энергией, так как даже в переводе «робуста» означает «сильный». Данный сорт кофе отличается большим содержанием кофеина. Кофеин обладает дренажным свойством, он улучшает кровоснабжение клеток, питает и обновляет их. Усиливает липолиз, оказывая корректирующее действие на жировые ткани, способствуя уменьшению видимых проявлений целлюлита. Кофе повышает эластичность кожи, тонизирует и защищает ее от вредного воздействия. Кофеин и антиоксиданты, содержащиеся в кофе восстанавливают кожу, укрепляют сосуды и улучшают кровообращение. Молотые кофейные зёрна способствуют омоложению эпидермиса, разглаживая морщинки и выравнивая кожный рельеф.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, '100% натуральный кофейный скраб с маслами Биоталь', 'Скраб для тела Biothal, созданный на основе средиземноморской соли, молодых кофейных зерен и комплекса натуральных масел отлично тонизирует и омолаживает кожу.', NULL, NULL, '100% натуральный скраб для тела, созданный на основе средиземноморской соли, молодых кофейных зерен и комплекса натуральных масел отлично тонизирует и омолаживает кожу.');
INSERT INTO `product_description` VALUES (71, 1, 'Гель для душа Средиземноморский Нероли', '<p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Гель для душа Средиземноморский Нероли окунет Вас в великолепное благоухание фруктовых садов Средиземноморья. Эфирное масло нероли окутывает изысканным тонким ароматом, а нежная текстура геля отлично удаляет загрязнения. Гель не содержит ПАВ синтетического происхождения, поэтому его можно применять ежедневно, без дискомфорта и чувства стянутости.</p><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-family: standard; text-align: center; text-transform: uppercase;\">АКТИВНЫЕ ИНГРЕДИЕНТЫ</h2><ul style=\"outline: 0px; margin-bottom: 10px; padding-left: 15px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><li style=\"outline: 0px; line-height: 20px !important;\">кокамидопропилбетаин,</li><li style=\"outline: 0px; line-height: 20px !important;\">коко глюкозид,</li><li style=\"outline: 0px; line-height: 20px !important;\">эфирное масло нероли,</li><li style=\"outline: 0px; line-height: 20px !important;\">эфирное масло бергамота,</li><li style=\"outline: 0px; line-height: 20px !important;\">протеины пшеницы,</li><li style=\"outline: 0px; line-height: 20px !important;\">комплекс Альго+</li></ul><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Кокамидопропилбетаин</u>&nbsp;- получают из жирных кислот кокосового масла. Этот ПАВ, обладающий антистатическим эффектом, великолепно очищает кожу, образуя стойкую пену. Признан одним из наиболее безопасных и щадящих ПАВ.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Эфирное масло нероли</u>&nbsp;считается одним из самых целебных, так как оно обладает бактерицидным и антисептическим действием. Масло нероли способно омолаживать увядшую тусклую кожу, устранять растяжки и другие кожные дефекты.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Протеины пшеницы</u>&nbsp;отлично усваиваются, стимулируют обновление и восстановление кожи, способствуют синтезу коллагеновых и эластиновых волокон.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Комплекс Альго +</u>&nbsp;насыщает кожу влагой и микроэлементами, оказывает подтягивающее и укрепляющее действие, повышает упругость и эластичность кожи.</p>', NULL, 'Натуральный гель для душа Биоталь Средиземноморский Нероли', 'Изысканный гель для душа Biothal Средиземноморский Нероли окунет Вас в атмосферу фруктовых садов Средиземноморья. Эфирное масло нероли, обладающее богатым насыщенным ароматом, подарит Вашему телу расслабление и наслаждение.', NULL, NULL, 'Изысканный гель для душа Средиземноморский Нероли окунет Вас в атмосферу фруктовых садов Средиземноморья. Эфирное масло нероли, обладающее богатым насыщенным ароматом, подарит Вашему телу расслабление и наслаждение.');
INSERT INTO `product_description` VALUES (72, 1, 'Успокаивающий тоник Ромашка-Бергамот', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Успокаивающий тоник Ромашка-Бергамот бережно очищает кожу, придает ей естественное сияние и помогает сбалансировать ее уровень ph, без использования агрессивных очищающих компонентов.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Уникальные свойства ромашки благотворно влияют на кожу, успокаивая ее и уменьшая воспаления. Кожа более светлая и сияющая уже через несколько применений.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Отсутствие спирта в продукте определяет его нежную структуру и натуральный аромат, положительно влияющий на состояние организма. Очищающий эффект достигнут с помощью коко глюкозида, природного компонента, отлично удаляющего загрязнения кожи.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Эфирное масло бергамота борется с появлением пигментных пятен и дарит защиту от ультрафиолетовых лучей вашей коже в течение всего дня. Благодаря уникальному водорослевому комплексу Альго + кожа насыщается питательными веществами и кислородом и дольше остается молодой и эластичной.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/ef-berg.jpg\" data-pagespeed-url-hash=\"2778213999\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло бергамота</h3>Масло бергамота считается одним из самых редких масел, поскольку это растение в своем первозданном виде встречается лишь на одном побережье Италии. Масло получают из цветков, листьев и кожуры плодов бергамота. Масло высоко ценится, так как выход продукта получается сравнительно небольшой. В состав масла входят вещества фурокумарины, способствующие выработке меланина – пигмента, защищающего кожу от воздействия ультрафиолетовых лучей. Также бергамот улучшает состояние сосудов, снимает воспаления и раздражения на коже.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго+<br style=\"outline: 0px;\"></h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/eks-rom.jpg\" data-pagespeed-url-hash=\"2403920559\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт ромашки<br style=\"outline: 0px;\"></h3>Ромашка лекарственная с давних времён используется как важный компонент косметических средств. Ее противовоспалительные свойства успокаивают кожу, заживляют мелкие ранки и снимают раздражение. Средства, имеющие экстракт ромашки в составе делают кожу не несколько тонов светлее, благодаря ее отбеливающему эффекту.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/koko-gluk.jpg\" data-pagespeed-url-hash=\"495552627\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Коко глюкозид<br style=\"outline: 0px;\"></h3>Натуральный компонент, созданный на основе кокосового масла и фруктозы, положительно влияет на состояние кожи, смягчая ее и защищая ее поверхность от потери белка и различных загрязнений.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><br style=\"outline: 0px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/lim-kisl.jpg\" data-pagespeed-url-hash=\"2189656491\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Лимонная кислота<br style=\"outline: 0px;\"></h3>Лимонная кислота – это незаменимый компонент практически всех косметических средств, так как ее уникальные свойства необходимы нашему организму, и в особенности коже. Лимонная кислота усиливает кислотность продукта, тем самым оптимизируя все его свойства. Данный компонент обеспечивает естественный пилинг, разрушая белковые связи между живыми и отмершими клетками, очищает кожу и делает ее гладкой и эластичной.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Успокаивающий тоник Ромашка-Бергамот Биоталь!', 'Натуральный успокаивающий тоник Biothal Ромашка-Бергамот бережно очищает кожу, придает ей естественное сияние и помогает сбалансировать ее уровень ph, без использования агрессивных очищающих компонентов.', NULL, NULL, 'Успокаивающий тоник Ромашка-Бергамот бережно очищает кожу, придает ей естественное сияние и помогает сбалансировать ее уровень ph, без использования агрессивных очищающих компонентов.');
INSERT INTO `product_description` VALUES (73, 1, 'Очищающий гель для умывания Сандал', '&lt;p&gt;Освободи свою кожу от шлаков и токсинов с инновационным гелем для умывания детокс. Тщательно подобранный состав продукта обеспечивает питание, тонус и сияние вашей кожи. Специалисты Biothal разработали особую формулу для очищения кожи на основе комбинации нескольких масел и водорослевого комплекса Альго+, которая глубоко проникает в кожу, выводит загрязнения и создает мощную защиту от негативного влияния окружающей среды.&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\nЭфирное масло сандалового дерева в комплексе с маслом босвеллии, бережно ухаживает за слабой, истощенной кожей, возвращая ей здоровое сияние и упругость.&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\nМолочная кислота обеспечивает натуральный пилинг, очищая кожу от омертвевших клеток и активизируя процессы регенерации.&lt;/p&gt;\r\n\r\n&lt;p&gt;\r\n\r\nВ условиях плохой экологии и постоянных стрессов кожа становится тусклой и ослабленной, рано проявляя признаки старения. Гель – детокс Сандал , призван обеспечить глубокое очищение кожи и надежную защиту.\r\n&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;/p&gt;&lt;div class=\"row ing\"&gt;\r\n    &lt;h2 style=\" text-align: center ;\"&gt;&lt;/h2&gt;&lt;p style=\"text-align: center;\"&gt;&lt;br&gt;&lt;/p&gt;\r\n    &lt;div class=\"col-sm-1\"&gt;\r\n        &lt;img src=\"/image/catalog/111/sandal.jpg\" style=\"width: 100%;\"&gt;\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-5\"&gt;\r\n        &lt;h3&gt;Эфирное масло сандалового дерева&lt;br&gt;&lt;/h3&gt;\r\n       Сандал – вечнозеленое дерево, произрастающее на острове Гаити, имеющее\r\nкрупные белые цветы и маслянистую кору. Масло имеет теплый древесный\r\nаромат и благотворно влияет на состояние кожи и организма в целом.\r\nСандаловое масло является богатым источником активных компонентов,\r\nнеобходимых коже, которые снимают раздражение, тонизируют кожу и\r\nсужают поры. Кроме того, масло сандалового дерева способствует\r\nрасслаблению и улучшению сна.\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-1\"&gt;\r\n        &lt;img src=\"/image/catalog/111/ladan.jpg\" style=\"width: 100%;\"&gt;\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-5\"&gt;\r\n        &lt;h3&gt;Эфирное масло ладана&lt;br&gt;&lt;/h3&gt;\r\n      Масло получают из смолы африканского дерева Босвеллии. Антисептические\r\nсвойства масла сделали его частым компонентом косметических средств по\r\nуходу за кожей. Ладан отбеливает кожу, способствует заживлению ранок и\r\nрубцов, тонизирует кожу и делает ее более эластичной.&lt;/div&gt;&lt;div class=\"col-sm-5\"&gt;&lt;br&gt;&lt;/div&gt;&lt;div class=\"col-sm-5\"&gt;&lt;br&gt;&lt;/div&gt;&lt;div class=\"col-sm-5\"&gt;&lt;br&gt;&lt;/div&gt;&lt;div class=\"col-sm-5\"&gt;\r\n    &lt;/div&gt;&lt;div class=\"col-sm-5\"&gt;&lt;br&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;div class=\"row ing\"&gt;\r\n    &lt;div class=\"col-sm-1\"&gt;\r\n        &lt;img src=\"/image/catalog/111/algo.jpg\" style=\"width: 100%;\"&gt;\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-5\"&gt;\r\n        &lt;h3&gt;Комплекс Альго+&lt;br&gt;&lt;/h3&gt;\r\n        Комплекс активных компонентов Альго+, широко известен своими\r\nцелебными свойствами. Основу комплекса составляют красные и бурые\r\nводоросли, имеющие удивительные способности к регенерации и\r\nобогащающие кожу необходимыми минералами и микроэлементами,\r\nнакопленными за многие века в глубине океанских вод. Инновационный\r\nкомплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает\r\nкожу от негативного влияния внешней среды.\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-1\"&gt;\r\n        &lt;img src=\"/image/catalog/111/lim-kisl.jpg\" style=\"width: 100%;\"&gt;\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-5\"&gt;\r\n        &lt;h3&gt;Лимонная кислота&lt;/h3&gt;\r\n      Лимонная кислота – это незаменимый компонент практически всех\r\nкосметических средств, так как ее уникальные свойства необходимы нашему\r\nорганизму, и в особенности коже. Лимонная кислота усиливает кислотность\r\nпродукта, тем самым оптимизируя все его свойства. Данный компонент\r\nобеспечивает естественный пилинг, разрушая белковые связи между живыми\r\nи отмершими клетками, очищает кожу и делает ее гладкой и эластичной.&lt;/div&gt;&lt;div class=\"col-sm-5\"&gt;&lt;br&gt;&lt;/div&gt;&lt;div class=\"col-sm-5\"&gt;&lt;br&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=\"row ing\"&gt;\r\n    &lt;div class=\"col-sm-1\"&gt;\r\n        &lt;img src=\"/image/catalog/111/koko-gluk.jpg\" style=\"width: 100%;\"&gt;\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-5\"&gt;\r\n        &lt;h3&gt;Коко глюкозид&lt;br&gt;&lt;/h3&gt;\r\n        Натуральный компонент, созданный на основе кокосового масла и фруктозы,\r\nположительно влияет на состояние кожи, смягчая ее и защищая ее\r\nповерхность от потери белка и различных загрязнений.\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-1\"&gt;\r\n        &lt;img src=\"/image/catalog/111/mol-kisl.jpg\" style=\"width: 100%;\"&gt;\r\n    &lt;/div&gt;\r\n    &lt;div class=\"col-sm-5\"&gt;\r\n        &lt;h3&gt;Молочная кислота&lt;br&gt;&lt;/h3&gt;\r\n       Молочная кислота относится к разряду фруктовых кислот и применяется в\r\nкосметических средствах для разных типов кожи. Основное действие\r\nмолочной кислоты заключается в ослаблении связей между клетками\r\nрогового слоя, в результате чего омертвевшие чаъстицы эпидермиса\r\nотшелушиваются, кожа омолаживается, рельеф поверхности кожи\r\nвыравнивается, становится меньше морщин.\r\n    &lt;/div&gt;&lt;div class=\"col-sm-5\"&gt;&lt;br&gt;&lt;/div&gt;\r\n&lt;/div&gt;', NULL, 'Очищающий гель Биоталь для умывания Сандал', 'Освободи свою кожу от шлаков и токсинов с инновационным натуральным детокс-гелем для умывания Сандал. Тщательно подобранный состав продукта обеспечивает питание, тонус и сияние вашей кожи. Biothal', NULL, NULL, 'Освободи свою кожу от шлаков и токсинов с инновационным детокс-гелем для умывания Сандал. Тщательно подобранный состав продукта обеспечивает питание, тонус и сияние вашей кожи.');
INSERT INTO `product_description` VALUES (74, 1, 'Очищающее молочко для чувствительной кожи', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Молочко с нежной текстурой для бережного увлажнения и сияния. Средство глубоко очищает кожу лица от макияжа и загрязнений, при этом способствуя сужению пор и выведению шлаков и токсинов из эпидермиса.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Большое содержание витамина Е в компонентах средства замедляет процесс старения кожи и способствует улучшению кровообращения в сосудах.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Экстракт лаванды очищает кожу от омертвевших частиц, способствует регенерации клеток. Экстракт ромашки обладает успокаивающим эффектом, он заживляет мелкие ранки и устраняет раздражения. Экстракт календулы нормализует работу сальных желез, сужает поры и повышает тонус кожи.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Молочко идеально подходит для чувствительной, нормальной и склонной к воспалениям кожи.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-pods.jpg\" data-pagespeed-url-hash=\"3789374328\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло семян подсолнечника<br style=\"outline: 0px;\"></h3>Масло из семян подсолнечника богато витамином Е, имеющим отличные антиоксидантные свойства, замедляющие процесс старения Вашей кожи. Масло нежно обволакивает кожу тонкой пленкой, которая не утяжеляет и не стягивает ее, при этом прекрасно увлажняет и способствует ускоренной регенерации клеток.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-lavand.jpg\" data-pagespeed-url-hash=\"1252365848\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт лаванды<br style=\"outline: 0px;\"></h3>Лаванда – вечнозеленое растение, обладающее нежным ароматом и уникальными свойствами, широко известными в косметологии. Экстракт лаванды дарит коже лёгкость, выводит шлаки и токсины, очищает ее от омертвевших частиц. Бережное воздействие масла позволяет использовать его для ухода за чувствительной и проблемной кожей.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/eks-kalend.jpg\" data-pagespeed-url-hash=\"537498612\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт календулы<br style=\"outline: 0px;\"></h3>Цветки календулы содержат большой процент органических кислот, среди которых салициловая, известная своими заживляющими свойствами. Экстракт календулы идеально подходит для жирной, проблемной кожи, так как вещества, содержащиеся в нем, сужают поры, нормализуют работу потовых желез и повышают тонус кожи.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xeks-rom.jpg.pagespeed.ic.eKl4yrnfbW.webp\" data-pagespeed-url-hash=\"2403920559\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт ромашки<br style=\"outline: 0px;\"></h3>Ромашка лекарственная с давних времён используется как важный компонент косметических средств. Ее противовоспалительные свойства успокаивают кожу, заживляют мелкие ранки и снимают раздражение. Средства, имеющие экстракт ромашки в составе, делают кожу чуть светлее, благодаря ее отбеливающему эффекту.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br style=\"outline: 0px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Algo+<br style=\"outline: 0px;\"></h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xtokoferol.jpg.pagespeed.ic.ySl3t0TEtZ.webp\" data-pagespeed-url-hash=\"1912770404\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Ацетат токоферола<br style=\"outline: 0px;\"></h3>Эффективный антиоксидант, предотвращающий окисление ненасыщенных липидов. Хорошо проникает в глубокие слои кожи, смягчает и очищает ее. Богат витамином Е необходимым для кровеносных сосудов и укрепления тканей организма.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Очищающее молочко для чувствительной кожи Биоталь', 'Натуральное молочко Biothal с нежной текстурой для бережного увлажнения и сияния. Средство глубоко очищает кожу лица от макияжа и загрязнений, при этом способствуя сужению пор и выведению шлаков и токсинов из эпидермиса.', NULL, NULL, 'Молочко с нежной текстурой для бережного увлажнения и сияния. Средство глубоко очищает кожу лица от макияжа и загрязнений, при этом способствуя сужению пор и выведению шлаков и токсинов из эпидермиса.');
INSERT INTO `product_description` VALUES (75, 1, 'Сыворотка-эликсир Питание и Увлажнение', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Сыворотка-эликсир Питание и Увлажнение - это сыворотка на основе драгоценных питательных масел, интенсивно увлажняющая и питающая кожу. Сыворотка насыщает кожу активными микроэлементами, повышая ее тонус и упругость. Она отлично выравнивает цвет лица, стимулирует выработку эластина и коллагена и снимает воспаления.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Благодаря целому комплексу природных масел, сыворотка дарит коже естественное сияние и молодость. Масло абрикосовой косточки насыщает кожу витаминами, нормализует работу сальных желез и улучшает цвет лица. Уникальные свойства жасминового масла подстраиваются под любой тип кожи, соответствуя потребностям каждого. Масло авокадо, благодаря содержанию в нем витамина А помогает сохранить коже молодой сияющий вид.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Сыворотка Эликсир питание увлажнение помогает коже сохранить упругость, эластичность и здоровье. Здоровая кожа – это красота и сияние.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-abrikosa.jpg\" data-pagespeed-url-hash=\"2228338664\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Абрикосовое масло</h3>Абрикосовое масло производят методом холодного отжима абрикосовых косточек. Полученная эссенция - это экологически чистый продукт натурального происхождения, богатый полиненасыщенными жирные кислоты, витаминами группы А, С, В, F и другими активными компонентами. Масло подходит для сухой, огрубевшей, лишенной влаги кожи. Благоприятное воздействие оказывает на чувствительную, проблемную и склонную к формированию прыщей кожу. Масло улучшает цвет лица, повышает эластичность кожи, убирает вялость и сухость, разглаживает морщинки.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/ef-ylang-ylang.jpg\" data-pagespeed-url-hash=\"1990554804\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло иланг- иланг</h3>Растение Иланг-иланг произрастает в азиатских тропиках. Оно имеет нежные крупные цветы, из которых методом паровой дистилляции получают масло. Масло иланг-иланг обладает комплексом полезных элементов. Благодаря содержанию натуральных спиртов, масло дезинфицирует кожу и снимает воспаление. Монотерпены тонизируют и оказывают антисептическое воздействие. Фенолы нормализуют циркуляцию крови, а кислоты защищают от влияния свободных радикалов и омолаживают кожу. Масло иланг-иланг подходит для всех типов кожи. Оно замедляет процессы старения, питает и увлажняет кожу.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/jasmine.jpg\" data-pagespeed-url-hash=\"1111757634\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло жасмина</h3>Для изготовления масла цветки жасмина собираются исключительно перед рассветом, так как в это время в лепестках растения содержится самое большое количество бесценного эфира. Данный продукт является самым дорогим из всех известных эфиролей, так как для получения килограмма экстракта, нужно переработать тонну лепестков. полезно для всех типов кожи. Оно отлично восстановит и омолодит структуру эпидермиса, устранит растяжки и рубцы. Масло подходит для всех типов кожи: возрастной коже оно дарит упругость, эластичность и свежесть, сухая кожа глубоко увлажняется, жирной коже масло помогает отрегулировать работу сальных желез, убрать воспаления и раздражения. Применение масла жасмина выравнивает цвет лица, заживляет трещинки, порезы, улучшает общее состояние эпидермиса.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/skvalan.jpg\" data-pagespeed-url-hash=\"3471432701\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Сквалан</h3>Сквалан – это ненасыщенный природный углеводород, который получают методом многократной дистилляции и фильтрования оливкового масла первого холодного отжима или масла семян амаранта. Молекулы вещества легко встраиваются в липидный слой кожи и укрепляют его, препятствуя испарению влаги. Сквалан делает кожу более эластичной, ускоряет ее регенерацию, стимулирует антиоксидантные процессы. За счет сходного состава с кожным жиром, молекулы сквалена глубоко впитываются в кожу, питая ее и насыщая полезными веществами. Скавалан интенсивно стимулирует клеточный рост и усиливает иммунитет.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-mindal.jpg.pagespeed.ic.zubr6g3R4g.webp\" data-pagespeed-url-hash=\"279319911\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Миндальное масло<br style=\"outline: 0px;\"></h3>Для получения миндального масла используются два вида миндаля: горький и сладкий. Миндальное масло является одним из базовых масел, на основе которых создаются косметические средства. Обилие витамина Е замедляет процесс старения клеток кожи, а витамин F нормализует работу сальных желёз и способствует очищению и сужению пор. Витамин А смягчает и увлажняет кожу. Миндальное масло благотворно влияет на кожу вокруг глаз, устраняя мимические морщинки и осветляя круги усталости.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-avocado.jpg\" data-pagespeed-url-hash=\"2808404183\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло авокадо</h3>Масло авокадо легко проникает в кожу, благодаря его сходному составу с комплексом жирных кислот, помогая ей восстанавливать естественное здоровое состояние. Масло авокадо устраняет шелушения, воспаления, замедляет процессы старения. Благодаря большому количеству витамина А, содержащемуся в авокадо, кожа дольше сохраняет молодость и сияние. Содержание в масле авокадо вещества авокутина снижает жирность кожи на треть через неделю с начала применения.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-vino.jpg\" data-pagespeed-url-hash=\"4278466014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло виноградной косточки</h3>Масло виноградной косточки не имеет холестерола в составе, так как добывается оно методом горячей экстракции. В виноградных косточках, из которых получают масло, содержится большой процент линолевой кислоты, которая контролирует насыщенность кожи влагой и ее способность к регенерации. Также масло имеет большое содержание витамина Е, который отлично питает кожу и омолаживает ее.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-lavand.jpg.pagespeed.ic.xMQrK0eIJr.webp\" data-pagespeed-url-hash=\"1252365848\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло лаванды</h3>Лаванда – вечнозеленое растение, обладающее нежным ароматом и уникальными свойствами, широко известными в косметологии. Экстракт лаванды дарит коже лёгкость, выходит шлаки и токсины, очищает ее от омертвевших частиц. Бережное воздействие масла позволяет использовать его для ухода чувствительной и проблемной кожей.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-petigreyda.jpg\" data-pagespeed-url-hash=\"2328868904\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло петтигрейна</h3>Масло петитгрейна - это цитрусовое масло, полученное из побегов и листьев апельсинового дерева. Масло имеет приятный аромат, который восстанавливает гармонию внутренних и физических сил и является источником легкости. Масло замедляет процессы старения: возрождает кожу от сухости, дряблости и провисаний, также способствует выработке коллагена в коже. Сохраняя влагу в клетках кожи, способствует нормализации водного баланса и насыщает кожу витаминами и органическими кислотами.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Сыворотка-эликсир Питание и Увлажнение - Biothal', 'Сыворотка Эликсир питание увлажнение Биоталь - это сыворотка на основе драгоценных питательных масел, интенсивно увлажняющая и питающая кожу. Сыворотка насыщает кожу активными микроэлементами, повышая ее тонус и упругость. Отлично подходит, как праймер по', NULL, NULL, 'Сыворотка-эликсир Питание и Увлажнение - это сыворотка на основе драгоценных питательных масел, интенсивно увлажняющая и питающая кожу. Сыворотка насыщает кожу активными микроэлементами, повышая ее тонус и упругость. Отлично подходит, как праймер под макияж');
INSERT INTO `product_description` VALUES (76, 1, 'Увлажняющий питательный крем для лица 24', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Увлажняющий питательный крем для лица с легкой текстурой, отлично проникает в глубокие слои кожи, насыщая клетки влагой и активными микроэлементами. Крем мгновенно увлажняет и питает, пробуждает энергию молодости кожи, устраняет тусклый цвет лица, делает кожу нежной и бархатистой.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Уникальный состав средства на основе натуральных масел и гиалуроновой кислоты обеспечивает здоровье и молодость вашей кожи. Молекулы гиалуроновой кислоты образуют в поверхностных слоях эпидермиса влагоудерживающий барьер, которы смягчает и разглаживает кожу. Морщинки и углубления в коже мгновенно заполняются гиалуроновой кислотой, рельефа кожи выравнивается. Благодаря большому содержанию витамина Е в масле семян подсолнечника и масле виноградной косточки, в клетках кожи запускаются процессы, замедляющие старение. Средство отлично подходит как для сухой увядшей кожи, так и для нормальной кожи.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Ваша увлажненная сияющая кожа на весь день после применения Увлажняющего питательного крема для лица 24.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\"><span style=\"outline: 0px; color: inherit; font-family: inherit;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></span></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br style=\"outline: 0px;\"></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-pods.jpg.pagespeed.ic.CZv8UqAQFh.webp\" data-pagespeed-url-hash=\"3789374328\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло семян подсолнечника</h3>Масло из семян подсолнечника богато витамином Е, имеющим отличные антиоксидантные свойства, замедляющие процесс старения кожи. Масло нежно обволакивает кожу тонкой пленкой, которая не утяжеляет и не стягивает ее, при этом прекрасно увлажняет и способствует ускоренной регенерации клеток. Отлично раскрывает свои целебные свойства на сухой и увядшей коже, увлажняя ее и восстанавливая изнутри.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-mindal.jpg.pagespeed.ic.zubr6g3R4g.webp\" data-pagespeed-url-hash=\"279319911\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Миндальное масло<br style=\"outline: 0px;\"></h3>Для получения миндального масла используются два вида миндаля: горький и сладкий. Миндальное масло является одним из базовых масел, на основе которых создаются косметические средства. Масло отлично подходит для ухода за нежной, чувствительной кожей. Проникая в глубокие слои эпидермиса, оно активизирует кровообращение. Кожа становится более гладкой и подтянутой, так как масло способствует выводу лишней жидкости и шлаков.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-vino.jpg.pagespeed.ic.7HtdVlLxo1.webp\" data-pagespeed-url-hash=\"4278466014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло виноградной косточки</h3>Масло виноградной косточки не имеет холестерола в составе, так как добывается оно методом горячей экстракции. В виноградных косточках, из которых получают масло, содержится большой процент линолевой кислоты, которая контролирует насыщенность кожи влагой и ее способность к регенерации. Также масло имеет большое содержание витамина Е, который отлично питает кожу и омолаживает ее.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xkislota.jpg.pagespeed.ic.ch7u87nAfJ.webp\" data-pagespeed-url-hash=\"2876933756\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Гиалуроновая кислота</h3>Гиалуроновая кислота является одним из составляющих нашей кожи, однако условия окружающей среды, плохая экология и стрессы являются причиной потери данного вещества кожей. Поэтому необходимо восстанавливать баланс гиалуроновой кислоты для поддержания водного баланса и обеспечения упругости и эластичности кожи. Данное вещество делает кожу более нежной, бархатистой и замедляет процесс старения.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго +</h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Натуральный увлажняющий питательный крем для лица 24 Биоталь', 'Увлажняющий питательный крем для лица Biothal с легкой текстурой, отлично проникает в глубокие слои кожи, насыщая клетки влагой и активными микроэлементами.', NULL, NULL, 'Увлажняющий питательный крем для лица с легкой текстурой, отлично проникает в глубокие слои кожи, насыщая клетки влагой и активными микроэлементами.');
INSERT INTO `product_description` VALUES (79, 1, 'Морской антивозрастной крем для лица 24', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Морской антивозрастной крем для лица 24 с гиалуроновой кислотой и пептидами, обогащенный целым комплексом активных компонентов, эффективно способствует омоложению кожи лица, запуская регенерацию клеток и насыщая их природными витаминами и минералами. Крем восстанавливает естественный баланс кожи, замедляет процессы старения, сохраняет свежесть и придаёт сияние коже лица.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">В условиях плохой экологии и постоянных стрессов кожа становится тусклой и ослабленной, появляются морщинки и изменяется овал лица. Крем морской эффект анти эйдж призван защитить кожу от вредных воздействий и вернуть ей молодость и красоту. Уникальный состав средства на основе водорослей бережно питает кожу, наделяя ее жизненной энергией океана. Морские растения, входящие в состав крема, имеют удивительные способности к регенерации клеток кожи. Пептидный комплекс- реконструирует матрикс дермы, проникая в глубокие слои кожи и замедляя процессы старения. Укрепление матрикса способствует восстановлению волокон коллагена, сохранению упругости и эластичности кожи. Благодаря маслу зародышей пшеницы, кожа сияет и становится светлее, морщинки разглаживаются.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">Активные компоненты<br><br></h2><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xkislota.jpg.pagespeed.ic.ch7u87nAfJ.webp\" data-pagespeed-url-hash=\"2876933756\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Гиалуроновая кислота</h3>Гиалуроновая кислота является одним из составляющих нашей кожи, однако условия окружающей среды, плохая экология и стрессы являются причиной потери данного вещества кожей. Поэтому необходимо восстанавливать баланс гиалуроновой кислоты для поддержания водного баланса и обеспечения упругости и эластичности кожи. Данное вещество делает кожу более нежной, бархатистой и замедляет процесс старения.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmatrik.jpg.pagespeed.ic.vCzgDabE2f.webp\" data-pagespeed-url-hash=\"1580498463\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Матриксил</h3>Матриксил - это естественный пептид, передающий клеткам кожи информацию об окружающей их среде. В случае распада структурных компонентов в клетках кожи, матриксил присоединяется к рецепторам клеток и заставляет их активировать синтез нужных веществ. С возрастом активность естественного матриксила становится ниже, поэтому специально синтезированный пептид необходим нашей коже, чтобы омолаживать ее и способствовать регенерации клеток.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго +</h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/kolagen.jpg\" data-pagespeed-url-hash=\"1401364102\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Коллаген</h3>Коллаген – это структурный белок кожи, обладающий защитными и увлажняющими функциями. С возрастом выработка естественного коллагена кожей сокращается, поэтому изменяется овал лица, появляются морщинки. Коллаген получают из водорослей или протеинов пшеницы, так как по структуре он схож с кожным коллагеном. Он гипоаллергенен, отлично усваивается кожей и положительно влияет на ее структуру. Коллаген омолаживает кожу, возвращая ей мягкость, упругость и естественное сияние.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-primulyi-vecherney.jpg\" data-pagespeed-url-hash=\"2029611307\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло примулы вечерней</h3>Масло примулы, раскрывающей свои лепестки после полуночи, получают из семян- способом холодного отжима. Такое масло высоко ценится, так как собирать семена примулы очень сложно. Масло содержит много жирных кислот, среди которых основное место занимает линолевая. Усиленная альфа- и гамма- компонентами она хорошо питает кожу и защищает её от ультрафиолетовых лучей. Кроме того, масло примулы имеет антиоксидантное, антивозрастное и заживляющее действие. Идеально подходит для сухой, чувствительной и стареющей кожи.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/peptidy.jpg\" data-pagespeed-url-hash=\"2132137476\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Пептидный комплекс</h3>Пептидный комплекс – это натуральный реконструктор матрикса дермы. Он замедляет процессы старения и обладает высокой проникающей способностью в глубокие слои кожи, стимулируя клетки молодости в выработке компонентов внеклеточного матрикса кожи. Укрепление матрикса способствует заживлению ранок на коже и восстановлению волокон коллагена. Также матрикс играет важную роль в сохранении эластичности и упругости кожи.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-shi.jpg\" data-pagespeed-url-hash=\"477139764\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло Ши<br style=\"outline: 0px;\"></h3>Извлечение масла из плодов дерева Ши является длительным процессом, так как это делается вручную, чтобы бережно сохранить полезные свойства и нежный ореховый аромат продукта. Масло ши смягчает кожу и защищает её от вредного воздействия среды. Входящие в состав масла каристеролы способствуют регенерации клеток, а жирные кислоты, которыми богато масло, питают и увлажняют кожу.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/136-1_olej-z-psenicnych-klicku.jpg\" data-pagespeed-url-hash=\"1960112840\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло зародышей пшеницы</h3>Для кожи масло зародышей пшеницы является источником полезных витаминов и микроэлементов. Ростки пшеницы содержат уникальный комплекс биологически активных соединений, питательных веществ и элементов, который благотворно влияю на состояние кожи, омолаживая и осветляя ее. В состав масла входят аминокислоты, жирные кислоты, витамины групп В, Е, А и D, а также такие микроэлементы, как цинк, селен, фосфор, марганец, железо и другие. Масло ростков пшеницы прекрасно снимает воспаления, борется с морщинами и тонизирует кожу.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-pods.jpg.pagespeed.ic.CZv8UqAQFh.webp\" data-pagespeed-url-hash=\"3789374328\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло семян подсолнечника</h3>Масло из семян подсолнечника богато витамином Е, имеющим отличные антиоксидантные свойства, помогает замедлить процессы старения кожи. Масло прекрасно увлажняет и способствует ускоренной регенерации клеток. Отлично раскрывает свои питательные свойства на сухой и увядшей коже, увлажняя ее и восстанавливая изнутри.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Морской антивозрастной крем для лица 24 - &quot;Биоталь&quot;', 'Морской антивозрастной крем для лица 24 - Biothal обогащенный целым комплексом активных компонентов, эффективно способствует омоложению кожи лица, запуская регенерацию клеток и насыщая их природными витаминами и минералами.', NULL, NULL, 'Морской антивозрастной крем для лица 24  обогащенный целым комплексом активных компонентов, эффективно способствует омоложению кожи лица, запуская регенерацию клеток и насыщая их природными витаминами и минералами.');
INSERT INTO `product_description` VALUES (80, 1, 'Соляной скраб для тела Дикая Роза', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Натуральный скраб для тела Дикая роза на основе средиземноморской соли и экстрактах драгоценных природных масел интенсивно обновляет кожу, бережно питая ее. Скраб эффективно удаляет омертвевшие клетки эпидермиса и очищает поры, позволяя коже вздохнуть с новой силой.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Средство отлично подходит для ухода за кожей любого типа и может стать базовым средством по уходу за телом. Морская соль питает кожу минералами и микроэлементами, выводя из клеток вредные вещества и загрязнения. Экстракт марокканской розы обладает противовоспалительными, охлаждающими, успокаивающими свойствами. Он тонизирует, подтягивает и омолаживает кожу, делая ее гладкой и упругой. Миндальное масло замедляет процесс старения клеток кожи, очищает и сужает поры. Благодаря маслу авокадо, питательные вещества проникают в глубокие слои кожи, интенсивно питая ее. Скраб отлично борется с целлюлитом и растяжками на коже, благодаря уникальным свойствам мандаринового масла. Масло жожоба, в свою очередь, делает кожу упругой и подтянутой.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xsea-salt.jpg.pagespeed.ic.AsSVujT234.webp\" data-pagespeed-url-hash=\"302412979\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Морская соль Средиземноморья<br style=\"outline: 0px;\"></h3>В морской соли содержится множество минералов и микроэлементов необходимых нашему организму и в особенности коже. Морская соль содержит такие вещества, как йод, кальций, магний, железо, калий, бром и другие. Морская соль стимулирует регенерацию тканей, успокаивает нервы и выводит вредные вещества из тканей организма. Она эффективно и бережно отшелушивает омертвевшие клетки эпидермиса, обновляя кожу. Морская соль способствует осветлению кожи и выравниванию ее рельефа.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xroza.jpg.pagespeed.ic.KOaz641dby.webp\" data-pagespeed-url-hash=\"2627804815\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт марокканской розы<br style=\"outline: 0px;\"></h3>Марокканская роза – это цветок, обладающий самым сильным и притягательным ароматом из всех разновидностей роз. Экстракт марокканской розы обладает противовоспалительными, охлаждающими, успокаивающими свойствами. Экстракт тонизирует, подтягивает и омолаживает кожу, делая ее гладкой и упругой. Благодаря высокому уровню жирных кислот, содержащихся в марокканской розе, она отлично восстанавливает защитные функции кожи, замедляет процесс старения клеток, подтягивает и тонизирует кожу.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br style=\"outline: 0px;\"></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-mindal.jpg.pagespeed.ic.zubr6g3R4g.webp\" data-pagespeed-url-hash=\"279319911\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Миндальное масло<br style=\"outline: 0px;\"></h3>Для получения миндального масла используются два вида миндаля: горький и сладкий. Миндальное масло является одним из базовых масел, на основе которых создаются косметические средства. Масло отлично подходит для ухода за нежной, чувствительной кожей. Проникая в глубокие слои эпидермиса, оно активизирует кровообращение. Кожа становится более гладкой и подтянутой, так как масло способствует выводу лишней жидкости и шлаков.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-avocado.jpg.pagespeed.ic.LucuDBT7v7.webp\" data-pagespeed-url-hash=\"2808404183\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло авокадо</h3>Масло авокадо легко проникает в кожу, благодаря его сходному составу с комплексом жирных кислот, помогая ей восстанавливать естественное здоровое состояние. Масло авокадо устраняет шелушения, воспаления, замедляет процессы старения. Благодаря большому количеству витамина А, содержащемуся в авокадо, кожа дольше сохраняет молодость и сияние. Содержание в масле авокадо вещества авокутина снижает жирность кожи на треть через неделю с начала применения.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xtokoferol.jpg.pagespeed.ic.ySl3t0TEtZ.webp\" data-pagespeed-url-hash=\"1912770404\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Токоферол</h3>Токоферол – это мощный антиаксидант, который проникая в глубокие слои эпидермиса гидролизуется в витамин Е. Защищает поверхность кожи от негативного воздействия солнечных лучей, препятствует разрушающему действию свободных радикалов. Выравнивает поверхность кожи, заживляет ранки и повреждения.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-shi.jpg.pagespeed.ic.s_Tq948yRX.webp\" data-pagespeed-url-hash=\"477139764\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло Ши<br style=\"outline: 0px;\"></h3>Извлечение масла из плодов дерева Ши является длительным процессом, так как это делается вручную, чтобы бережно сохранить полезные свойства и нежный ореховый аромат продукта. Масло ши смягчает кожу и защищает её от вредного воздействия среды. Входящие в состав масла каристеролы способствуют регенерации клеток, а жирные кислоты, которыми богато масло, питают и увлажняют кожу.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-olivka.jpg.pagespeed.ic.nJC4a6ufM3.webp\" data-pagespeed-url-hash=\"1965914910\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Оливковое масло</h3>Оливковое масло первого отжима считается самым полезным и богатым витаминами из разнообразных оливковых масел. Оливковое масло богато скваланом,&nbsp;веществом, которое интенсивно увлажняет кожу. Также, оливковое масло имеет в своем составе много кальция, железа, меди, витамины&nbsp;А, D&nbsp;и&nbsp;Е. Содержащиеся в масле витамины А и D участвуют в обновлении эпидермиса, отшелушивают ороговевшие участки кожи и защищают от загрязнений окружающей среды. Витамин A делает кожу увлажненной и эластичной.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xkoka.jpg.pagespeed.ic.9_r8vAxbJw.webp\" data-pagespeed-url-hash=\"2618847877\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Кокосовое масло</h3>Для получения кокосового масла необходимо дождаться, когда кокос созреет до необходимого состояния. Масло получают из мякоти кокоса, которую спрессовывают, чтобы отделить масло. Продукт обладает твёрдой консистенцией, белым цветом, нежно-сладким ароматом и приятным ореховым вкусом. В состав кокосового масла входят ненасыщенные жирные кислоты, фитостеролы, витамины, минералы и гиалуроновая кислота. Масло заметно улучшает состояния кожи и волос. Оно великолепно впитывается в кожу любого типа, увлажняя, смягчая, восстанавливая и улучшая эластичность кожи. Разглаживает морщинки и защищает от вредоносных влияний окружающей среды.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Натуральный соляной скраб для тела Дикая Роза Биоталь', 'Натуральный скраб для тела Дикая роза Biothal на основе средиземноморской соли и экстрактах драгоценных природных масел интенсивно обновляет кожу, бережно питая ее. Скраб эффективно удаляет омертвевшие клетки эпидермиса и очищает поры', NULL, NULL, 'Натуральный скраб для тела Дикая роза на основе средиземноморской соли и экстрактах драгоценных природных масел интенсивно обновляет кожу, бережно питая ее. Скраб эффективно удаляет омертвевшие клетки эпидермиса и очищает поры.');
INSERT INTO `product_description` VALUES (81, 1, 'Масло Антистресс', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Уникальное масло Антистресс, созданное на основе комплекса целебных природных масел, которые благотворно влияют на состояние кожи и организма в целом. Масло Антистресс оказывает на организм расслабляющее действие, даря коже легкость и увлажнение.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Масло Антистресс, благодаря маслам и экстрактам лекарственных растений бережно ухаживает за кожей, просветляет мысли и снимает нервное напряжение. Экстракт ромашки благотворно влияет на кожу, заживляя ранки и раздражения. Экстракт мяты охлаждает кожу и улучшает кровообращение и состояние сосудов. Масло грейпфрута делает кожу упругой и подтянутой, а масло шалфея создает надежную защиту на поверхности кожи, которая предохраняет ее от внешних негативных воздействий. Лаванда оказывает успокаивающее действие на организм благодаря нежному умиротворяющему аромату, погружая тело в расслабленное состояние. Благодаря уникальному составу масла Анитстресс, оно оказывает благотворное воздействие на нервную систему, улучшает настроение, устраняет нервозы, стрессы и напряжение.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-mindal.jpg.pagespeed.ic.zubr6g3R4g.webp\" data-pagespeed-url-hash=\"279319911\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Миндальное масло<br style=\"outline: 0px;\"></h3>Для получения миндального масла используются два вида миндаля: горький и сладкий. Миндальное масло является одним из базовых масел, на основе которых создаются косметические средства. Масло отлично подходит для ухода за нежной, чувствительной кожей. Проникая в глубокие слои эпидермиса, оно активизирует кровообращение. Кожа становится более гладкой и подтянутой, так как масло способствует выводу лишней жидкости и шлаков.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xtokoferol.jpg.pagespeed.ic.ySl3t0TEtZ.webp\" data-pagespeed-url-hash=\"1912770404\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Токоферол</h3>Токоферол – это мощный антиаксидант, который проникая в глубокие слои эпидермиса гидролизуется в витамин Е. Защищает поверхность кожи от негативного воздействия солнечных лучей, препятствует разрушающему действию свободных радикалов. Выравнивает поверхность кожи, заживляет ранки и повреждения.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-lavand.jpg.pagespeed.ic.xMQrK0eIJr.webp\" data-pagespeed-url-hash=\"1252365848\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло лаванды</h3>Лаванда – вечнозеленое растение, обладающее нежным ароматом и уникальными свойствами, широко известными в косметологии. Эфирное масло лаванды дарит коже лёгкость, выходит шлаки и токсины, очищает ее от омертвевших частиц. Бережное воздействие масла позволяет использовать его для ухода чувствительной и проблемной кожей.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xeks-rozm.jpg.pagespeed.ic.Mno6fsNuyA.webp\" data-pagespeed-url-hash=\"1641811147\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло розмарина</h3>Вечнозеленое растение розмарин в древние времена считалось поистине волшебным, благодаря своим целительным свойствам. Оно обладает антисептическим действием, а так же способностью сокращать и стягивать поры. Экстракт розмарина разглаживает кожу, придаёт ей большую эластичность, убирает рубцы и пигментные пятна.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xgr.png.pagespeed.ic.Sh61deg4hc.webp\" data-pagespeed-url-hash=\"1680844122\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло грейпфрута</h3>Грейпфрут – это вечнозеленое растение произрастающее исключительно в тропическом климате. Масло грейпфрута является отличным тонизирующим средством для кожи, оно восстанавливает водный баланс, снимает отеки и помогает избавляться от целлюлита. Масло грейпфрута способствует сохранению упругости и гладкости кожи, тормозя действие фермента эластазы, разрушающего волокна коллагена и эластина.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xeks-rom.jpg.pagespeed.ic.eKl4yrnfbW.webp\" data-pagespeed-url-hash=\"2403920559\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт ромашки</h3>Ромашка лекарственная с давних времён используется как важный компонент косметических средств. Ее противовоспалительные свойства успокаивают кожу, заживляют мелкие ранки и снимают раздражение. Средства, имеющие экстракт ромашки в составе делают кожу не несколько тонов светлее, благодаря ее отбеливающему эффекту.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xefirnoe-maslo-shalfeya.jpg.pagespeed.ic.Wbrd41gAzp.webp\" data-pagespeed-url-hash=\"3790775860\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло шалфея</h3>Эфирное масло шалфея получают из листьев растения способом дистилляции. Свойства шалфея благотворно влияют на жирную, комбинированную и увядающую кожу. В составе шалфея находятся карнозная и урсоловая кислоты, обладающие противомикробным и противовоспалительным действием. Эти кислоты защищают кожу от вредных воздействий и ультрафиолетовых лучей. Вещества флавноиды способны предохранять клетки от повреждений.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xpersikovoe-maslo-1.jpg.pagespeed.ic.NZNVFbQoqX.webp\" data-pagespeed-url-hash=\"1041930473\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло персиковых косточек</h3>Масло персиковых косточек получают методом холодного прессования ядер плода персика. Богатый состав масла поддерживает упругость и эластичность кожи, разглаживает морщины и выравнивает кожный рельеф. Полезно для чувствительной кожи, благодаря содержания витамина Е, который обладает антиоксидантным действием, витамина А, который поддерживает целостность клеток кожи, витамина В15, который, в свою очередь, обладает высокой биологической активностью.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/ship.png\" data-pagespeed-url-hash=\"1990908151\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт шиповника</h3>Масло шиповника содержит целый комплекс витаминов и жирных кислот, минералов и микроэлементов. Жирные кислоты, такие как олеиновая, линолевая, линоленовая, входящие в состав растения отвечают за гладкость и упругость кожи, увлажнение и защиту от негативных факторов окружающей среды. Высокое содержание антиоксидантов в шиповнике позволяет значительно замедлить процессы старения кожи, разгладить морщинки и улучшить цвет лица. Также экстракт шиповника обладает противовоспалительными, противомикробными и регенерирующими свойствами, он способствует заживлению ранок, устранению растяжек и шрамов.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/jekstrakt-mjaty.jpg\" data-pagespeed-url-hash=\"2409015040\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт мяты</h3>Основным действующим компонентом экстракта мяты является ментол. Он прекрасно охлаждает и освежает кожу, стимулирует кровообращение и укрепляет сосуды, успокаивает раздраженную кожу и нормализует работу сальных желез. Кроме того, мята содержит органические кислоты, аскорбиновую кислоту, флавоноиды и микроэлементы. Экстракт мяты освежает и охлаждает кожу, снимает раздражения и успокаивает воспаления, смягчает кожу и обезбаливает.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Масло Антистресс Биоталь', 'Уникальное масло Biothal Антистресс, созданное на основе комплекса целебных природных масел, которые благотворно влияют на состояние кожи и организма в целом. Масло Антистресс оказывает на организм расслабляющее действие, даря коже легкость и увлажнение.', NULL, NULL, 'Уникальное масло Антистресс, созданное на основе комплекса целебных природных масел, которые благотворно влияют на состояние кожи и организма в целом. Масло Антистресс оказывает на организм расслабляющее действие, даря коже легкость и увлажнение.');
INSERT INTO `product_description` VALUES (83, 1, 'Омолаживающая питательная маска для лица Роза', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Маска мгновенный эффект. Обладает тонизирующими, подтягивающими свойствами, стимулирует выработку коллагена, активизирует процессы регенерации клеток кожи, повышает упругость и эластичность. Сокращает морщины, увлажняет и устраняет отёчность и признаки усталости.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Омолаживающая маска на основе белой глины, и растительных масел виноградной косточки и авокадо нежно ухаживает за кожей, замедляя процессы старения. Богатый аромат дамасской розы дарит спокойствие и расслабление. Обогащённая гиалуроновой кислотой, маска восстанавливает водный баланс кожи, возвращая ей здоровый блеск и упругость.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Уникальный водорослевый комплекс Альго+ дарит коже жизненную силу океана и насыщает ее морскими минералами. Маска идеально подходит для кожи, ослабленной в результате таких факторов, как плохая экология, стрессы, курение, нехватка витаминов.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/roza.jpg\" data-pagespeed-url-hash=\"2627804815\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Экстракт дамасской розы&nbsp;<br style=\"outline: 0px;\"></h3>Дамасская роза является одним из наиболее ценных растений, применяемых для ухода за кожей, так как в состав ее лепестков входит целый комплекс витаминов и минеральных веществ. Основным свойством экстракта розы является омоложение клеток кожи. Данный экстракт имеет наименьшую токсичность, поэтому средства на его основе рекомендуются для чувствительной, склонной к воспалениям кожи.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-vino.jpg.pagespeed.ic.7HtdVlLxo1.webp\" data-pagespeed-url-hash=\"4278466014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло виноградной косточки<br style=\"outline: 0px;\"></h3>Масло виноградной косточки не имеет холестерола в составе, так как добывается оно методом горячей экстракции. В виноградных косточках, из которых получают масло, содержится большой процент линолевой кислоты, которая контролирует насыщенность кожи влагой и ее способность к регенерации. Также масло имеет большое содержание витамина Е, который отлично питает кожу и омолаживает ее.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br style=\"outline: 0px;\"></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-avocado.jpg.pagespeed.ic.LucuDBT7v7.webp\" data-pagespeed-url-hash=\"2808404183\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло авокадо<br style=\"outline: 0px;\"></h3>Масло авокадо легко проникает в кожу, благодаря его сходному составу с комплексом жирных кислот, помогая ей восстанавливать естественное здоровое состояние. Масло авокадо устраняет шелушения, воспаления, замедляет процессы старения. Благодаря большому количеству витамина А, содержащемуся в авокадо, кожа дольше сохраняет молодость и сияние. Содержание в масле авокадо вещества авокутина снижает жирность кожи на треть через неделю с начала применения.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/white-glina.jpg\" data-pagespeed-url-hash=\"2511734538\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Белая глина</h3>Белая глина или каолин представляет собой соединение оксидов алюминия и кремния. Состав глины богат микроэлементами, такими как магний, цинк, кальций и другие. Основой белой глины являются мелкие частицы, обладающие абсорбирующим эффектом, в результате чего кожа избавляется от токсинов и загрязнений. Регулярное использование улучшает цвет кожи, активизирует выработку коллагена, контуры лица становятся более очерченные.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xkislota.jpg.pagespeed.ic.ch7u87nAfJ.webp\" data-pagespeed-url-hash=\"2876933756\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Гиалуроновая кислота&nbsp;<br style=\"outline: 0px;\"></h3>Гиалуроновая кислота является одним из составляющих нашей кожи, однако условия окружающей среды, плохая экология и стрессы являются причиной потери данного вещества кожей. Поэтому необходимо восстанавливать баланс гиалуроновой кислоты для поддержания водного баланса и обеспечения упругости и эластичности кожи. Данное вещество делает кожу более нежной, бархатистой и замедляет процесс старения.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго+<br style=\"outline: 0px;\"></h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br style=\"outline: 0px;\"></div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xpeptidy.jpg.pagespeed.ic._HpgVvJOXA.webp\" data-pagespeed-url-hash=\"2132137476\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Пептидный комплекс<br style=\"outline: 0px;\"></h3>Пептидный комплекс – это натуральный реконструктор матрикса дермы. Он замедляет процессы старения и обладает высокой проникающей способностью в глубокие слои кожи, стимулируя клетки молодости в выработке компонентов внеклеточного матрикса кожи. Укрепление матрикса способствует заживлению ранок на коже и восстановлению волокон коллагена. Также матрикс играет важную роль в сохранении эластичности и упругости кожи. Пептидный комплекс активирует производство собственного коллагена, разглаживание кожу и уменьшает размер и количество морщин.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Омолаживающая питательная  маска для лица Роза - Биоталь', 'Омолаживающая натуральная маска &quot;Biothal&quot; на основе белой глины, и растительных масел виноградной косточки и авокадо нежно ухаживает за кожей, замедляя процессы старения.', NULL, NULL, 'Омолаживающая маска на основе белой глины, и растительных масел виноградной косточки и авокадо нежно ухаживает за кожей, замедляя процессы старения.');
INSERT INTO `product_description` VALUES (86, 1, 'Гель для душа Прованс Лаванда', '<p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Окунуться в божественные ароматы цветущих полей Прованса позволит гель для душа с успокаивающим запахом лаванды. Гель сделан на основе натурального ПАВ, состоящего из фруктозы и кокосового масла. Именно поэтому он не стягивает и не раздражает кожу, даже самую чувствительную и сухую, и подходит для ежедневного использования.</p><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-family: standard; text-align: center; text-transform: uppercase;\">АКТИВНЫЕ ИНГРЕДИЕНТЫ</h2><ul style=\"outline: 0px; margin-bottom: 10px; padding-left: 15px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><li style=\"outline: 0px; line-height: 20px !important;\">кокамидопропилбетаин,</li><li style=\"outline: 0px; line-height: 20px !important;\">коко глюкозид,</li><li style=\"outline: 0px; line-height: 20px !important;\">эфирное масло лаванды,</li><li style=\"outline: 0px; line-height: 20px !important;\">протеины пшеницы,</li><li style=\"outline: 0px; line-height: 20px !important;\">комплекс Альго+</li></ul><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Кокамидопропилбетаин</u>&nbsp;- вещество, полученное из жирных кислот кокосового масла. Этот натуральный ПАВ признан одним из наиболее безопасных и щадящих. Он эффективно очищает кожу, обладает антистатическим эффектом.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Эфирное масло лаванды</u>&nbsp;отлично тонизирует кожу, повышает ее упругость и эластичность. Обладает очищающими свойствами, избавляя кожу от лишнего жира и загрязнений. Аромат лаванды снимает напряжение и улучшает сон.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Протеины пшеницы</u>&nbsp;хорошо усваиваются кожей, стимулируют ее обновление и восстановление, способствуют синтезу коллагеновых и эластиновых волокон.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Комплекс Альго +</u>&nbsp;насыщает кожу влагой и микроэлементами, оказывает подтягивающее и укрепляющее действие, повышает упругость и эластичность кожи.</p>', NULL, 'Гель для душа Биоталь Прованс Лаванда', 'Нежный успокаивающий гель для душа Biothal Прованс Лаванда окутает Вас ароматом цветущих полей Прованса и подарит Вашей коже легкость и чистоту. Продукт сделан на основе растительного ПАВ, состоящего из жирных кислот кокосового масла.', NULL, NULL, 'Нежный успокаивающий гель для душа Прованс Лаванда окутает Вас ароматом цветущих полей Прованса и подарит Вашей коже легкость и чистоту. Продукт сделан на основе растительного ПАВ, состоящего из жирных кислот кокосового масла.');
INSERT INTO `product_description` VALUES (95, 1, 'Интенсивный крем \"Антицеллюлит\"', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Тонизирующий крем для тела на основе масел зеленого кофе, грейпфрута и можжевельника интенсивно борется с видимыми проявлениями целлюлита, повышает упругость и гладкость кожи. Благодаря активному составу средство глубоко проникает в слои эпидермиса, активизируя процессы омоложения и восстановления клеток. Обладает разогревающим эффектом.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Интенсивный крем анти-целлюлит эффективен для борьбы с целлюлитом, растяжками и другими кожными дефектами. Благодаря витамину В3, входящему в состав крема, рельеф кожи выравнивается, «бугорки» и «ямочки» разглаживаются. Крем способствует интенсивному лифтингу, очищает и увлажняет кожу. Масло можжевельника усиливает клеточный метаболизм, делает кожу упругой и сияющей. Большой процент кофеина в составе средства корректирует жировые ткани организма, способствуя похудению. Приятная нежная текстура средства отлично подходит для массажа проблемных участков тела, средство быстро впитывается, не оставляя утяжеляющего слоя на коже.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ</h2><p style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\"><br></p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xef-mogh.jpg.pagespeed.ic.CCcIRpmf6x.webp\" data-pagespeed-url-hash=\"416284098\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Эфирное масло можжевельника</h3>Масло можжевельника получают из ягод этого реликтового растения, с древности славившегося своими антисептическими и очищающими свойствами. Масло отлично тонизирует кожу, способствуя её обновлению, и увеличивает клеточный метаболизм. Активные компоненты, содержащиеся в ягодах растения, выравнивают цвет кожи, повышают её упругость и омолаживают её изнутри. Эффективно борется с целлюлитом, а также используется для его профилактики.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго+<br style=\"outline: 0px;\"></h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xgr.png.pagespeed.ic.Sh61deg4hc.webp\" data-pagespeed-url-hash=\"1680844122\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло Грейпфрута<br style=\"outline: 0px;\"></h3>Грейпфрут – это вечнозеленое растение произрастающее исключительно в тропическом климате. Масло грейпфрута является отличным тонизирующим средством для кожи, оно восстанавливает водный баланс, снимает отеки и помогает избавляться от целлюлита. Масло грейпфрута способствует сохранению упругости и гладкости кожи, тормозя действие фермента эластазы, разрушающего волокна коллагена и эластина.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/niacianid.jpg\" data-pagespeed-url-hash=\"1818336183\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Ниацинамид<br style=\"outline: 0px;\"></h3>Ниацинамид - это водорастворимый витамин В3, который ускоряет регенерацию клеток кожи, улучшает ее эластичность и усиливает барьерные функции. Ниацинамид эффективно выравнивает рельеф кожи и осветляет пигментные пятна. Способствует стимуляции синтеза коллагена и помогает снизить потерю влаги кожей, а также увлажняет, сужает поры и успокаивает. Применяется для ухода за чувствительной кожей.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/gree-kofe.jpg\" data-pagespeed-url-hash=\"495952736\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло зелёных кофейных зёрен</h3>Масло из зеленого кофе добывают путем холодного прессования зеленых кофейных зерен. Масло, полученное из свежих зерен, имеет намного больше кофеина, чем жареные зерна кофе. Кофеин обладает дренажным свойством, он улучшает кровоснабжение клеток, питает и обновляет их. Усиливает липолиз, оказывая корректирующее действие на жировые ткани, способствуя уменьшению видимых проявлений целлюлита. Масло из свежих зерен имеет легкую текстуру, оно отлично впитывается в кожу, не забивая поры. Масло зеленого кофе благодаря мультивитаминному комплексу и гиалуроновой кислоте питает и увлажняет кожу, также способствует выработке природного эластана. Масло богато полиненасыщенными кислотами, восстанавливающими клетки верхних слоев кожи, а также хлорогеновой кислотой, которая способствует похудению.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/kofein.jpg\" data-pagespeed-url-hash=\"2508178043\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Кофеин</h3>Кофеин активно выводит из организма лишнюю жидкость, тем самым уменьшая отечность. Кофеин тонизирует и укрепляет сосуды и улучшает кровообращение. Помимо липолитического и дренажного действия, кофеин оказывает на кожу и омолаживающий эффект, активизируя синтез холестерина – липида, играющего важную роль в защитной функции кожи.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Интенсивный натуральный крем &quot;Антицеллюлит&quot; Биоталь', 'Тонизирующий крем для тела Biothal, на основе масел зеленого кофе, грейпфрута и можжевельника интенсивно борется с видимыми проявлениями целлюлита, повышает упругость и гладкость кожи.', NULL, NULL, 'Тонизирующий крем для тела на основе масел зеленого кофе, грейпфрута и можжевельника интенсивно борется с видимыми проявлениями целлюлита, повышает упругость и гладкость кожи.');
INSERT INTO `product_description` VALUES (99, 1, 'Дренажная щётка для сухого массажа', '<p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; color: rgba(0, 0, 0, 0.87); font-size: 16px;\"><span style=\"font-family: &quot;Times New Roman&quot;;\">Методика сухого массажа с помощью щетки является максимально эффективной благодаря активному лимфодренажному и антицеллюлитному действию, который она оказывает на кожу. Благодаря этим свойствам, кожа становится гладкой и эластичной, а контуры тела – четкими и подтянутыми</span>.</p><p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; color: rgba(0, 0, 0, 0.87); font-size: 16px;\"><span style=\"font-family: &quot;Times New Roman&quot;;\">Дренажная щётка для сухого массажа Biothal создана из экологически чистых материалов, которые не вызывают раздражений и аллергий. Основа щетки - натуральное дерево, которое не расслаивается с течением времени. Щетина сделана из волокон мексиканского кактуса, которые интенсивно отшелушивают омертвевшие клетки эпидермиса и стимулируют обменные процессы. Жесткость щетины объясняет то, что она отлично очищает кожу и избавляет ее от неровностей и дефектов. Природные материалы, из которых изготовлено изделие деликатно прорабатывают проблемные зоны и стимулируют микроциркуляцию. Овальная форма щетки является эргономичной, так как ее удобно держать в руке, передвигать по телу, закруглённые края щетки исключают риск травмирования кожи. Массаж щеткой активизирует кровообращение и лимфоотток, отлично разогревая кожу. Также, щетка отшелушивает ороговевшие клетки, делая кожу более упругой и эластичной.</span></p><p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; color: rgba(0, 0, 0, 0.87); font-size: 16px;\"><span style=\"font-family: &quot;Times New Roman&quot;;\">Хотите получить идеальное подтянутое тело с четкими контурами и упругой кожей? Массаж сухой щеткой – самый простой и эффективный способ этого достичь!</span></p><p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; color: rgba(0, 0, 0, 0.87); font-size: 16px;\"></p><p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; color: rgba(0, 0, 0, 0.87); font-size: 16px;\"><u style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63); outline: 0px;\"><br style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: inherit;\"></u></p><p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 16px; font-family: Manrope, sans-serif; color: rgba(0, 0, 0, 0.87); font-size: 16px;\"><u style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63); outline: 0px;\">Основа щетки</u><span style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63);\">&nbsp;</span><span style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: standard; color: rgb(41, 37, 63);\">— натуральное дерево —&nbsp; бук.</span></p><p style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin-bottom: 10px; font-family: standard; font-size: 16px; outline: 0px; color: rgb(41, 37, 63); line-height: 20px !important;\"><u style=\"background-repeat: no-repeat; box-sizing: inherit; padding: 0px; margin: 0px; font-family: inherit; outline: 0px;\">Щетина</u>&nbsp;сделана из волокон мексиканского кактуса, которые интенсивно отшелушивают омертвевшие клетки эпидермиса и стимулируют обменные процессы</p><p>\r\n                    </p>', NULL, 'Дренажная щётка для сухого массажа Биоталь', 'Методика сухого массажа с помощью щетки является максимально эффективной благодаря активному лимфодренажному и антицеллюлитному действию, который она оказывает на кожу. Натуральная массажная щетка Biothal', NULL, NULL, 'Методика сухого массажа с помощью щетки является максимально эффективной благодаря активному лимфодренажному и антицеллюлитному действию, который она оказывает на кожу.');
INSERT INTO `product_description` VALUES (104, 1, 'Гель для душа Королевский Пачули', '<p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Гель для душа с протеинами пшеницы и маслом пачули – по-настоящему королевский уход для Вашей кожи. Гель не содержит ПАВ синтетического происхождения, поэтому не сушит и не стягивает кожу. Волнующий аромат королевской пачули пробуждает чувства и наполняет блаженством.</p><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-family: standard; text-align: center; text-transform: uppercase;\">АКТИВНЫЕ ИНГРЕДИЕНТЫ</h2><ul style=\"outline: 0px; margin-bottom: 10px; padding-left: 15px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><li style=\"outline: 0px; line-height: 20px !important;\">кокамидопропилбетаин,</li><li style=\"outline: 0px; line-height: 20px !important;\">коко глюкозид,</li><li style=\"outline: 0px; line-height: 20px !important;\">масло пачули,</li><li style=\"outline: 0px; line-height: 20px !important;\">протеины пшеницы,</li><li style=\"outline: 0px; line-height: 20px !important;\">комплекс Альго+</li></ul><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Кокамидопропилбетаин</u>&nbsp;- вырабатывается из жирных кислот кокосового масла. Способствует эффективному очищению кожи, имеет антистатический эффект. Данный компонент является одним из самых безопасных и щадящих кожу ПАВ.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Протеины пшеницы</u>&nbsp;хорошо усваиваются, обновляют и восстанавливают кожу, стимулируют выработку коллагеновых и эластиновых волокон</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Масло пачули</u>&nbsp;обладает противоотечным эффектом и выводит из кожи лишнюю жидкость, обладает освежающим и разглаживающим эффектом.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Комплекс Альго +</u>&nbsp;насыщает кожу влагой и микроэлементами, оказывает подтягивающее и укрепляющее действие, повышает упругость и эластичность кожи.</p>', NULL, 'Гель для душа  Королевский Пачули - &quot;Биоталь&quot;', 'Натуральный Гель для душа Biothal королевский Пачули  на основе пшеничного протеина бережно ухаживает за кожей, обновляя и восстанавливая ее.', NULL, NULL, 'Гель для душа королевский Пачули на основе пшеничного протеина бережно ухаживает за кожей, обновляя и восстанавливая ее.');
INSERT INTO `product_description` VALUES (105, 1, 'Гель для душа Грейпфрут', '<p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Мечтаете побывать в знойных тропиках? Просто примите душ, используя гель Грейпфрут! Экзотический аромат грейпрфрута поднимет настроение и придаст энергии. Смело используете его каждый день – в составе нет искусственных ПАВ, которые вызывают чувство стянутости и сухости.</p><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-family: standard; text-align: center; text-transform: uppercase;\">АКТИВНЫЕ ИНГРЕДИЕНТЫ</h2><ul style=\"outline: 0px; margin-bottom: 10px; padding-left: 15px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><li style=\"outline: 0px; line-height: 20px !important;\">кокамидопропилбетаин,</li><li style=\"outline: 0px; line-height: 20px !important;\">коко глюкозид,</li><li style=\"outline: 0px; line-height: 20px !important;\">эфирное масло грейпфрута,</li><li style=\"outline: 0px; line-height: 20px !important;\">протеины пшеницы,</li><li style=\"outline: 0px; line-height: 20px !important;\">комплекс Альго+</li></ul><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Кокамидопропилбетаин</u>&nbsp;получают из жирных кислот кокосового масла. Он эффективно очищает кожу, обладает антистатическим эффектом. Один из самых безопасных и щадящих кожу ПАВ.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Эфирное масло грейпфрута</u>&nbsp;способствует сохранению упругости и гладкости кожи, тормозя действие фермента эластазы, разрушающего волокна коллагена и эластина.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Протеины пшеницы</u>&nbsp;хорошо усваиваются, обновляют и восстанавливают кожу, стимулируют синтез коллагена и эластина.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"><u style=\"outline: 0px;\">Комплекс Альго +</u>&nbsp;насыщает кожу влагой и микроэлементами, оказывает подтягивающее и укрепляющее действие, повышает упругость и эластичность кожи.</p>', NULL, 'Гель для душа Грейпфрут - &quot;Биоталь&quot;', 'Натуральный бодрящий гель для душа на основе грейпфрутового масла перенесет Вас в знойные тропики, оставив на коже яркий экзотический аромат свежих цитрусов, и подарит мощный энергетический заряд на весь день.', NULL, NULL, 'Бодрящий гель для душа на основе грейпфрутового масла перенесет Вас в знойные тропики, оставив на коже яркий экзотический аромат свежих цитрусов, и подарит мощный энергетический заряд на весь день.');
INSERT INTO `product_description` VALUES (106, 1, 'Лосьон для тела Королевский Пачули', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Мягкая формула лосьона для тела Королевский Пачули бережно ухаживает за кожей, смягчая и разглаживая ее. Благодаря маслу ши, клетки кожи насыщаются влагой и ускоряются процессы обновления эпидермиса. Комплекс эфирных масел способствует активной регенерации клеток, улучшает кровообращение и дарит коже бархатистую структуру.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Водорослевой комплекс Альго+ насыщает клетки кожи активными минералами и микроэлементами. Эфирные масла пачули и миндаля нежно заботятся о поврежденной, чувствительной коже, восстанавливая ее и делая более подтянутой и эластичной. Жирные кислоты, которыми богато масло ши активно питают и увлажняют кожу.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Роскошный аромат восточной экзотики и интенсивное увлажнение Вашей кожи с Лосьоном для тела Королевская Пачули.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 style=\"outline: 0px; line-height: 1.3; margin-top: 20px; margin-bottom: 15px; font-size: 24px; padding: 10px 0px 0px; color: rgb(51, 51, 51); text-align: center; font-weight: 600 !important;\"><br></h2><p style=\"outline: 0px; margin-bottom: 10px; text-align: center; line-height: 20px !important;\"><br style=\"outline: 0px;\"></p><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-shi.jpg.pagespeed.ic.s_Tq948yRX.webp\" data-pagespeed-url-hash=\"477139764\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло Ши<br style=\"outline: 0px;\"></h3>Извлечение масла из плодов дерева Ши является длительным процессом, так как это делается вручную, чтобы бережно сохранить полезные свойства и нежный ореховый аромат продукта. Масло ши смягчает кожу и защищает её от вредного воздействия среды. Входящие в состав масла каристеролы способствуют регенерации клеток, а жирные кислоты, которыми богато масло, питают и увлажняют кожу.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xalgo.jpg.pagespeed.ic.aZ5QH7DQGe.webp\" data-pagespeed-url-hash=\"3686132014\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Комплекс Альго+<br style=\"outline: 0px;\"></h3>Комплекс активных компонентов Альго+, широко известен своими целебными свойствами. Основу комплекса составляют красные и бурые водоросли, имеющие удивительные способности к регенерации и обогащающие кожу необходимыми минералами и микроэлементами, накопленными за многие века в глубине океанских вод. Инновационный комплекс Альго+ обеспечивает быструю регенерацию клеток кожи, насыщает их кислородом и полезными микроэлементами, а так же защищает кожу от негативного влияния внешней среды.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xmaslo-mindal.jpg.pagespeed.ic.zubr6g3R4g.webp\" data-pagespeed-url-hash=\"279319911\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Миндальное масло<br style=\"outline: 0px;\"></h3>Для получения миндального масла используются два вида миндаля: горький и сладкий. Миндальное масло является одним из базовых масел, на основе которых создаются косметические средства. Масло отлично подходит для ухода за нежной, чувствительной кожей. Проникая в глубокие слои эпидермиса, оно активизирует кровообращение. Кожа становится более гладкой и подтянутой, так как масло способствует выводу лишней жидкости и шлаков.</div><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/maslo-pachuly.jpg\" data-pagespeed-url-hash=\"520848640\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Масло Пачули</h3>Пачули – многолетний кустарник с бело-сиреневыми цветами, произрастающий на Филиппинских островах. Правильно растёт и развивается только в плодородной, обогащённой почве. Масло добывают из молодых листьев растения. Масло избавляет тело от отеков и применяется для получения лифтинг-эффекта лица и тела. Имеет антицеллюлитное действие и отлично выравнивает кожный рельеф.</div></div><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; padding-bottom: 0px; margin-bottom: 0px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><div class=\"col-sm-1\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 134.609px;\"><img src=\"https://biothal.ua/image/catalog/111/xtokoferol.jpg.pagespeed.ic.ySl3t0TEtZ.webp\" data-pagespeed-url-hash=\"1912770404\" style=\"outline: 0px; image-rendering: auto; display: block; float: right; width: 91.6875px;\"></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><h3 style=\"outline: 0px; line-height: 1.3; margin-top: 0px; margin-bottom: 15px; font-size: 18px; padding: 10px 0px 0px; color: rgb(51, 51, 51); font-weight: 600 !important;\">Витамин Е</h3>Витамин Е - это мощный антиоксидант, который проникает в глубокие слои эпидермиса. Защищает поверхность кожи от негативного воздействия солнечных лучей, препятствует разрушающему действию свободных радикалов. Выравнивает поверхность кожи, заживляет ранки и повреждения.</div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div><div class=\"col-sm-5\" style=\"outline: 0px; padding-right: 10px; padding-left: 10px; width: 673.109px;\"><br></div></div>', NULL, 'Лосьон для тела Королевский Пачули - &quot;Биоталь&quot;', 'Мягкая формула лосьона для тела Королевский Пачули Biothal бережно ухаживает за кожей, смягчая и разглаживая ее. Благодаря маслу ши, клетки кожи насыщаются влагой и ускоряются процессы обновления эпидермиса.', NULL, NULL, 'Мягкая формула лосьона для тела Королевский Пачули бережно ухаживает за кожей, смягчая и разглаживая ее. Благодаря маслу ши, клетки кожи насыщаются влагой и ускоряются процессы обновления эпидермиса.');
INSERT INTO `product_description` VALUES (107, 1, 'Лосьон для тела Грейпфрут', '<p style=\"outline: 0px; margin-bottom: 10px; padding-top: 0px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Подарите Вашей коже интенсивное увлажнение и гладкость с Лосьоном для тела Грейпфрут. Легкая текстура лосьона подарит коже блеск и сияние, не создавая утяжеляющего слоя на ее поверхности. Мощные антиоксиданты, входящие в состав средства насыщают кожу витамином Е, который продлевает молодость и упругость кожи. Уникальные свойства масла ши подарят коже легкость и увлажнение.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Водорослевый комплекс Альго+ способствует регенерации клеток кожи, насыщает их кислородом и дарит естественную жизненную силу и энергию океана. Благодаря сбалансированному сочетанию эфирных масел, лосьон бережно ухаживает даже за самой чувствительной кожей, защищая ее от свободных радикалов и загрязнений. Эффективная формула лосьона на основе густого масла дерева ши, эфирных масел грейпфрута и миндаля, обеспечит глубокое длительное увлажнение и надежную защиту Вашей кожи.</p><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\">Тонус и сияние Вашей кожи с лосьоном для тела Грейпфрут.</p><h2 style=\"margin-top: 20px; margin-bottom: 15px; line-height: 1.3; font-size: 24px; font-family: standard; color: rgb(51, 51, 51); outline: 0px; padding: 10px 0px 0px; text-align: center; font-weight: 600 !important;\">АКТИВНЫЕ ИНГРEДИЕНТЫ<br><br></h2><p style=\"outline: 0px; margin-bottom: 10px; font-size: 16px; color: rgb(41, 37, 63); font-family: standard; line-height: 20px !important;\"></p><div class=\"row ing\" style=\"outline: 0px; margin-right: -10px; margin-left: -10px; color: rgb(41, 37, 63); font-family: standard; font-size: 16px;\"><h2 s