INSERT INTO `products` VALUES (50, 'my_link', '2018-05-26 08:15:33', '2021-07-22 09:21:02', 1, 6, NULL, 680.00, 340, '102', 45, NULL, 1, 1, 3, 1, 'Мицеллярная вода Роза', 'Мицеллярная вода содержит комплекс необходимых элементов для тщательного и в тоже время деликатного очищения кожи от макияжа и загрязнений. ', 'Micellar Rose Cleansing Water', '200 мл.', 1, 0, 0, 0);
INSERT INTO `products` VALUES (51, 'my_link', '2018-05-26 08:15:49', '2021-07-22 09:21:02', 2, 6, NULL, 1360.00, 680, '4627145990551', 7, NULL, 1, 1, 10, 1, 'Сыворотка-эликсир &quot;Антиэйдж&quot;', 'Омолаживающая антивозрастная сыворотка Элексир Анти Эйдж создана для возвращения коже молодости и свежести. Эта обновляющая сыворотка с гиалуроновой кислотой и пептидным комплексом борется с возрастными изменениями кожи. ', 'Anti-Age Elixir', '30 мл.', 1, 0, 0, 0);
INSERT INTO `products` VALUES (57, 'my_link', '2018-05-26 08:15:58', '2021-07-26 18:33:28', 3, 6, NULL, 1056.00, 528, '120', 25, NULL, 1, 1, 21, 2, 'Крем для тела Омолаживающий и Питательный', 'Питательный омолаживающий крем для тела на основе масел дерева ши и вечерней примулы бережно защищает кожу от внешних воздействий и эффективно увлажняет сухую, увядшую кожу. ', 'Rejuvenating Nutrient Body Cream', '200 мл.', 1, 0, 0, 0);
INSERT INTO `products` VALUES (59, 'my_link', '2018-05-26 08:16:04', '2021-07-22 09:21:02', 4, 6, NULL, 1112.00, 556, '108', 9, NULL, 1, 1, 9, 1, 'Детокс-пилинг скраб для лица Петитгрейн', 'Пилинг скраб для лица с богатой нежной текстурой призван освободить Вашу кожу от тусклости и вялости. Благодаря его уникальной формуле и активным компонентам кожа гладкая и сияющая уже после первого применения.', 'Gentle Detox Peel and Scrub Petitgrain', '100 мл.', 1, 0, 0, 0);
INSERT INTO `products` VALUES (62, 'my_link', '2018-05-26 08:16:04', '2021-08-13 12:18:29', 5, 6, NULL, 680.00, 340, '7930077270064', 8, NULL, 1, 1, 24, 2, 'Соль для ванн Розмарин-Лаванда', 'Расслабляющая Соль для ванн розмарин лаванда дарит телу приятную легкость, успокаивает кожу и окутывает Вас нежным ароматом природных масел. ', 'Bath Salt Rosemary Lavender', '500 мл.', 1, 0, 0, 0);
INSERT INTO `products` VALUES (63, 'my_link', '2018-05-26 08:16:04', '2021-07-22 09:21:02', 6, 6, NULL, 680.00, 340, '7930077270071', 1, NULL, 1, 1, 23, 1, 'Соль для ванн Дикая Роза', 'Соль для ванн дикая роза создана специально для того, чтобы дарить наслаждение и расслабление Вашему телу. Чувственный аромат дикой розы окутает Вас с ног до головы, располагая организм к расслаблению и умиротворению. ', 'Bath Salt Wild Rose', '500 мл.', 1, 0, 0, 0);
INSERT INTO `products` VALUES (64, 'my_link', '2018-05-26 08:16:04', '2021-07-22 09:21:02', 7, 6, NULL, 1104.00, 552, '128', 1, NULL, 1, 1, 29, 1, 'Масло Антицеллюлит', 'Масло Антицеллюлит интенсивно воздействует на кожу, благодаря активным компонентам, проникающим в глубокие слои эпидермиса. Кожа становиться подтянутой и эластичной, «ямочки» и «бугорки» разглаживаются, тон кожи выравнивается. ', 'Anti Cellulite Active Body oil', '150 мл.', 1, 0, 1, 0);
INSERT INTO `products` VALUES (65, 'my_link', '2018-05-26 08:16:04', '2021-07-22 09:21:02', 8, 6, NULL, 1104.00, 552, '127', 14, NULL, 1, 1, 31, 1, 'Масло кокосовое', 'Кокосовое масло является незаменимым средством для здоровья кожи, волос и общего состояния организма. Оно возрождает молодость и красоту кожи, подтягивая морщинки. Кожа становиться мягкой и увлажненной. ', 'Coconut Oil', '150 мл.', 1, 0, 0, 0);
