INSERT INTO `products` VALUES (69, 'my_link', '2018-05-26 08:16:10', '2021-07-22 09:21:02', 10, 6, NULL, 1112.00, 556, '126', 7, NULL, 1, 1, 27, 1, 'Скраб кофейный с маслами', '100% натуральный скраб для тела, созданный на основе средиземноморской соли, молодых кофейных зерен и комплекса натуральных масел отлично тонизирует и омолаживает кожу.', 'Coffee oil Scrub', '380 мл.', 1, 1, 1, 0);
INSERT INTO `products` VALUES (71, 'my_link', '2018-05-26 08:16:10', '2021-07-22 09:21:02', 11, 6, NULL, 680.00, 340, '115', 99, NULL, 1, 1, 16, 1, 'Гель для душа Средиземноморский Нероли', 'Изысканный гель для душа Средиземноморский Нероли окунет Вас в атмосферу фруктовых садов Средиземноморья. Эфирное масло нероли, обладающее богатым насыщенным ароматом, подарит Вашему телу расслабление и наслаждение.', 'Mediterranean Neroli Shower Gel', '300 мл.', 1, 0, 1, 0);
