INSERT INTO `products` VALUES (73, 'my_link', '2018-05-26 08:16:10', '2021-07-30 10:11:44', 13, 6, NULL, 680.00, 340, '103', 99, NULL, 0, 1, 4, 2, 'Очищающий гель для умывания Сандал', 'Освободи свою кожу от шлаков и токсинов с инновационным детокс-гелем для умывания Сандал. Тщательно подобранный состав продукта обеспечивает питание, тонус и сияние вашей кожи.', 'Sandalwood Facial Cleansing Gel', '200 мл.', 1, 0, 1, 0);
