INSERT INTO `products` VALUES (83, 'my_link', '2018-10-01 08:43:52', '2021-07-22 09:21:02', 21, 6, NULL, 1240.00, 620, '105', 47, NULL, 1, 1, 6, 1, 'Омолаживающая питательная маска для лица Роза', 'Омолаживающая маска на основе белой глины, и растительных масел виноградной косточки и авокадо нежно ухаживает за кожей, замедляя процессы старения. ', 'Rose Rejuvenating Mask', '100 мл.', 1, 0, 0, 0);
INSERT INTO `products` VALUES (86, 'my_link', '2018-10-01 08:50:46', '2021-07-22 09:21:02', 22, 6, NULL, 680.00, 340, '4627145990568', 99, NULL, 1, 1, 15, 1, 'Гель для душа Прованс Лаванда', 'Нежный успокаивающий гель для душа Прованс Лаванда окутает Вас ароматом цветущих полей Прованса и подарит Вашей коже легкость и чистоту. Продукт сделан на основе растительного ПАВ, состоящего из жирных кислот кокосового масла.', 'Provence Lavender Shower Gel', '300 мл.', 1, 0, 0, 0);
