INSERT INTO `products` VALUES (127, 'my_link', '2019-12-19 09:08:22', '2021-07-22 09:21:02', 41, 6, NULL, 680.00, 340, '134', 1, NULL, 1, 1, 11, 1, 'Жидкое мыло для тела и рук Герань Мандарин', 'Натуральное мыло на основе масел герани и мандарина бережно очистит загрязнения, успокоит и смягчит кожу, окажет противовоспалительное и антибактериальное действие. Мыло не пересушивает кожу.', 'Geranium & Mandarin Hand Body Wash', '300 мл.', 1, 0, 0, 0);
INSERT INTO `products` VALUES (128, 'my_link', '2019-12-19 09:43:19', '2021-07-22 09:21:02', 42, 6, NULL, 1112.00, 556, '133', 9, NULL, 1, 1, 1, 1, 'Крем масло для тела Цитрус Бабассу', 'Легкий и воздушный баттер на основе масел цитрусовых фруктов и экзотического бабассу обладает высокой увлажняющей, питательной и смягчающей способностью. Подходит для ежедневного ухода за кожей и проведения массажей.', 'Body Butter Citrus Babassu', '380 мл.', 1, 1, 0, 0);
INSERT INTO `products` VALUES (129, 'my_link', '2019-12-19 10:56:07', '2021-07-22 09:21:02', 43, 6, NULL, 932.00, 466, '135', 2, NULL, 1, 1, 1, 1, 'Крем для тела и рук Герань Мандарин с витамином E', 'Интенсивное увлажнение и питание для сухой, шелушащейся кожи рук и тела. Сливочная текстура крема моментально смягчает и успокаивает сухую кожу, дарит надежную защиту хрупкой, поврежденной коже. Идеальное решение для ухода в холодное время года!', 'Geranium & Mandarin Vitamin E Hand Body Cream', '300 мл.', 1, 0, 0, 0);
